INSERT INTO `activity` VALUES (1,'Những Người Bạn Đầu Tiên','2019-04-27 15:37:08.356595','2019-04-27 15:37:08.356601',1,1,'<p style=\"margin-right:0cm; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#8e44ad\"><strong><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 01 trong chuy&ecirc;n đề NHẬP M&Ocirc;N KỸ THUẬT.&nbsp; Ở hoạt động n&agrave;y c&aacute;c bạn sẽ được t&igrave;m hiểu về c&aacute;c c&ocirc;ng cụ, linh kiện hỗ trợ cho việc lắp r&aacute;p kỹ thuật. </span></span></strong></span></span></p>\r\n','<p style=\"margin-right:0cm; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#8e44ad\"><strong><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 01 trong chuy&ecirc;n đề NHẬP M&Ocirc;N KỸ THUẬT.&nbsp; Ở hoạt động n&agrave;y c&aacute;c bạn sẽ được t&igrave;m hiểu về c&aacute;c c&ocirc;ng cụ, linh kiện hỗ trợ cho việc lắp r&aacute;p kỹ thuật. Từ đ&oacute; c&oacute; những lưu &yacute; chu ri&ecirc;ng m&igrave;nh trong việc sử dụng v&agrave; th&aacute;o lắp linh kiện khi cần. </span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><strong><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;, đ&acirc;y l&agrave; hoạt động học tập kh&ocirc;ng bắt buộc.</span></span></strong></span></span></p>\r\n','/media/phanloai_bulong_daioc.pdf',1,'',5,2,3,NULL),(2,'Những Kỹ Thuật Đơn Giản','2019-04-27 17:27:03.852601','2019-04-27 17:27:03.852607',1,1,'<p style=\"margin-right:0cm; text-align:justify\"><span style=\"font-size:20px\"><em><strong><span style=\"color:#16a085\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 02 của chuy&ecirc;n đề NHẬP M&Ocirc;N KỸ THUẬT. Ở hoạt động n&agrave;y, c&aacute;c bạn sẽ được t&igrave;m hiểu c&aacute;c kỹ thuật sử dụng mỏ h&agrave;n, tuốt d&acirc;y để h&agrave;n, gắn linh kiện.&nbsp;</span></span></span></strong></em></span></p>\r\n','<p style=\"text-align:justify\"><span style=\"font-size:20px\"><em><strong><span style=\"color:#16a085\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 02 của chuy&ecirc;n đề NHẬP M&Ocirc;N KỸ THUẬT. Ở hoạt động n&agrave;y, c&aacute;c bạn sẽ được t&igrave;m hiểu c&aacute;c kỹ thuật sử dụng mỏ h&agrave;n, tuốt d&acirc;y để h&agrave;n, gắn linh kiện. Đ&acirc;y l&agrave; hoạt động đơn giản nhưng c&oacute; tầm quan trọng to lớn trong việc t&igrave;m hiểu c&aacute;c nội dung kh&oacute;a học sau n&agrave;y. </span></span></span></strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><em><strong><span style=\"color:#e74c3c\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;: đ&acirc;y l&agrave; hoạt động học tập kh&ocirc;ng bắt buộc.</span></span></span></strong></em></span></p>\r\n','https://www.youtube.com/embed/Tm_haBXwzHs',2,'',5,3,3,NULL),(3,'Biết Người - Biết Ta','2019-04-27 17:33:34.233837','2019-04-27 17:33:34.233844',1,1,'<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><strong><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 01 của chuy&ecirc;n đề học tập số 02.&nbsp;</span></span></span></strong></span></p>\r\n','<p style=\"text-align:justify\"><span style=\"color:#16a085\"><strong><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 01 của chuy&ecirc;n đề học tập số 02. Ở b&agrave;i học n&agrave;y, ch&uacute;ng ta sẽ c&ugrave;ng nhau t&igrave;m hiểu những ứng dụng thực tế của arduino trong cuộc sống. </span></span></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><strong><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&acirc;y l&agrave; hoạt động học tập kh&ocirc;ng bắt buộc.</span></span></span></strong></span></p>\r\n','https://www.youtube.com/embed/u2rs_8g2QA8',1,'',5,3,4,NULL),(4,'Nhập Môn Lập Trình C++','2019-04-27 17:35:43.213934','2019-04-27 17:35:43.213940',1,1,'<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><em><strong><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&uacute;ng như t&ecirc;n gọi của n&oacute;, hoạt động n&agrave;y cung cấp cho c&aacute;c bạn c&aacute;c hướng dẫn chi tiết v&agrave; kh&aacute;i qu&aacute;t nhất về ng&ocirc;n ngữ lập tr&igrave;nh C++ v&agrave; những thuật to&aacute;n, c&ocirc;ng cụ, d&agrave;nh cho người mới t&igrave;m hiểu.&nbsp;</span></span></span></strong></em></span></p>\r\n','<p style=\"text-align:justify\"><span style=\"color:#16a085\"><em><strong><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&uacute;ng như t&ecirc;n gọi của n&oacute;, hoạt động n&agrave;y cung cấp cho c&aacute;c bạn c&aacute;c hướng dẫn chi tiết v&agrave; kh&aacute;i qu&aacute;t nhất về ng&ocirc;n ngữ lập tr&igrave;nh C++ v&agrave; những thuật to&aacute;n, c&ocirc;ng cụ, d&agrave;nh cho người mới t&igrave;m hiểu. H&atilde;y cố gắng t&igrave;m hiểu tất cả những g&igrave; bạn c&oacute; thể. Đ&acirc;y l&agrave; hoạt động ti&ecirc;n quyết v&agrave; c&oacute; tầm ảnh hưởng quan trọng đến việc tham gia c&aacute;c hoạt động học tập sau n&agrave;y. </span></span></span></strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><em><strong><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;: Đ&acirc;y l&agrave; hoạt động học tập kh&ocirc;ng bắt buộc.</span></span></span></strong></em></span></p>\r\n','/media/nhap_mon_lap_trinh.pdf',1,'',5,2,5,NULL),(5,'Kho Tàng Vô Giá','2019-04-27 17:44:00.253666','2019-04-27 17:44:00.253672',1,1,'<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&acirc;y l&agrave; hoạt động học tập số 01 trong chuỗi c&aacute;c hoạt động học tập của chuy&ecirc;n đề học tập số 2. Đ&acirc;y l&agrave; hoạt động được thiết kế với chức năng ch&iacute;nh l&agrave; cung cấp những nguồn t&agrave;i liệu, hoặc những kiến thức mở rộng c&oacute; li&ecirc;n quan đến nội dung b&agrave;i học.&nbsp;</span></em></strong></span></span></span></p>\r\n','<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&acirc;y l&agrave; hoạt động học tập số 01 trong chuỗi c&aacute;c hoạt động học tập của chuy&ecirc;n đề học tập số 2. Đ&acirc;y l&agrave; hoạt động được thiết kế với chức năng ch&iacute;nh l&agrave; cung cấp những nguồn t&agrave;i liệu, hoặc những kiến thức mở rộng c&oacute; li&ecirc;n quan đến nội dung b&agrave;i học. B&ecirc;n cạnh đ&oacute;, chuy&ecirc;n mục &ldquo;Document - Kho T&agrave;ng V&ocirc; Gi&aacute;&rdquo; cũng l&agrave; một nơi chứ đựng nhiều điều bất ngờ v&agrave; th&uacute; vị.</span></em></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085; font-family:Times New Roman, serif\"><span style=\"font-size:20px\"><strong><em>T&agrave;i liệu cần thiết:</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Times New Roman, serif\"><span style=\"font-size:20px\"><strong><em><a href=\"/media/2019/04/27/doc_them_arduino_ide.pdf\"><span style=\"color:#8e44ad\">Đọc th&ecirc;m về Arduino IDE</span></a></em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Times New Roman, serif\"><span style=\"font-size:20px\"><strong><em><a href=\"/media/2019/04/27/doc_them_ve_arduino_uno_6aRfU1s.pdf\"><span style=\"color:#8e44ad\">Arduino UNO - Những điều chưa biết</span></a></em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;, đ&acirc;y l&agrave; hoạt động học tập bắt buộc, kh&ocirc;ng t&iacute;nh điểm.</span></em></strong></span></span></span></p>\r\n','/media/tai_lieu_tong_hop_arduno_5836.pdf',1,'',5,2,6,NULL),(6,'Người Bạn Mới Quen','2019-04-27 17:45:52.755661','2019-04-27 17:45:52.755666',1,1,'<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Arduino &ndash; Người Bạn Mới Quen l&agrave; hoạt động học tập số 2 trong chuy&ecirc;n đề học tập số 1.&nbsp;</span></em></strong></span></span></span></p>\r\n','<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Arduino &ndash; Người Bạn Mới Quen l&agrave; hoạt động học tập số 2 trong chuy&ecirc;n đề học tập số 1. Ở hoạt động n&agrave;y, C&aacute;c bạn sẽ được t&igrave;m hiểu về nguồn gốc xuất xứ, lịch sử h&igrave;nh th&agrave;nh, những ưu điểm, nhược điểm của Arduino trong cuộc sống v&agrave; học tập hằng ng&agrave;y. </span></em></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;, đ&acirc;y l&agrave; hoạt động học tập bắt buộc, kh&ocirc;ng t&iacute;nh điểm.</span></em></strong></span></span></span></p>\r\n','/media/unzip/hd_02_b01_nguoibanmoi/story_flash.html',2,'',5,4,6,NULL),(7,'Sự Thật Bất Ngờ','2019-04-27 17:48:04.571933','2019-04-27 17:48:04.571939',1,1,'<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#16a085\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 3 trong chuỗi c&aacute;c hoạt động học tập của chuy&ecirc;n đề học tập số 1.&nbsp;</span></em></strong></span></span></span></p>\r\n','<p style=\"margin-right:0cm; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#16a085\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o mừng c&aacute;c bạn đ&atilde; đến với hoạt động học tập số 3 trong chuỗi c&aacute;c hoạt động học tập của chuy&ecirc;n đề học tập số 1. Ở hoạt động n&agrave;y, ch&uacute;ng ta sẽ được t&igrave;m hiểu về Arduino UNO &ndash; Một trong những Board mạch Arduino được sử dụng rộng r&atilde;i v&agrave; phổ biến nhất. Những th&ocirc;ng tin về th&ocirc;ng số kỹ thuật, Input, Output, ph&acirc;n loại, v.v. Đều sẽ được đề cập v&agrave; hướng dẫn cụ thể ở hoạt động n&agrave;y. </span></em></strong></span></span></span></p>\r\n\r\n<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;, đ&acirc;y l&agrave; hoạt động học tập bắt buộc, kh&ocirc;ng t&iacute;nh điểm.</span></em></strong></span></span></span></p>\r\n','/media/unzip/hd03_b01_suthat_batngo/story_flash.html',3,'',5,4,6,NULL),(8,'Arduino IDE','2019-04-27 17:51:23.937883','2019-04-27 17:51:23.937890',1,1,'<p><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&acirc;y l&agrave; hoạt động học tập số 3 trong chuỗi c&aacute;c hoạt động học tập của chuy&ecirc;n đề học tập số 1. </span></em></strong></span></span></span></p>\r\n','<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&acirc;y l&agrave; hoạt động học tập số 3 trong chuỗi c&aacute;c hoạt động học tập của chuy&ecirc;n đề học tập số 1. Tham gia hoạt động n&agrave;y, c&aacute;c bạn sẽ được ch&uacute;ng t&ocirc;i hướng dẫn tuần tự v&agrave; chi tiết từng thao t&aacute;c c&agrave;i đặt v&agrave; thiết đặc t&ugrave;y chỉnh cho Arduino IDE.</span></em></strong></span></span></span></p>\r\n\r\n<p style=\"margin-right:0cm; text-align:justify\"><span style=\"color:#16a085; font-family:Times New Roman, serif\"><span style=\"font-size:20px\"><strong><em>Hướng dẫn th&ecirc;m:</em></strong></span></span></p>\r\n\r\n<p style=\"margin-right:0cm; text-align:justify\"><span style=\"font-family:Times New Roman, serif\"><span style=\"font-size:20px\">