INSERT INTO `competition` VALUES (1,'ARDUINO – TÀI NĂNG TRẺ TUỔI LẦN I','ARDUINO TÀI NĂNG TRẺ TUỔI là cuộc thi được khởi động nhằm một đích tạo ra sân chơi vui vẻ, lành mạnh cho các bạn trẻ, các em học sinh có niềm đam mê to lớn với lặp trình phần cứng','<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:28.0pt\"><span style=\"font-family:&quot;UTM Alba Matter&quot;,serif\"><span style=\"color:#00b0f0\">ARDUINO </span></span></span><span style=\"font-size:28.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#00b0f0\">&ndash;</span></span></span><span style=\"font-size:28.0pt\"><span style=\"font-family:&quot;UTM Alba Matter&quot;,serif\"><span style=\"color:#00b0f0\"> T&Agrave;I NĂNG TRẺ TUỔI LẦN I</span></span></span></span></span></p>\r\n\r\n<div style=\"border-bottom:solid #1ba1e2 1.5pt; padding:0cm 0cm 0cm 0cm\">\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n</div>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:darkorange\">I. Giới thiệu</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ARDUINO T&Agrave;I NĂNG TRẺ TUỔI l&agrave; cuộc thi được khởi động nhằm một đ&iacute;ch tạo ra s&acirc;n chơi vui vẻ, l&agrave;nh mạnh cho c&aacute;c bạn trẻ, c&aacute;c em học sinh c&oacute; niềm đam m&ecirc; to lớn với lặp tr&igrave;nh phần cứng n&oacute;i ri&ecirc;ng v&agrave; lập tr&igrave;nh tr&ecirc;n board mạch Arduino n&oacute;i chung. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tham gia cuộc thi n&agrave;y, c&aacute;c bạn sẽ được giao lưu v&agrave; học hỏi kinh nghiệm từ nhiều bạn đến từ khắp mọi nơi tr&ecirc;n đất nước, những bạn c&oacute; c&ugrave;ng sở th&iacute;ch v&agrave; niềm đam m&ecirc;.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><img src=\"/media/2019/04/28/image-20190428175730-1.png\" style=\"height:412px; width:688px\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:darkorange\">II. Thể lệ cuộc thi</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:teal\">1. Mục đ&iacute;ch của cuộc thi</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#8e44ad\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Khuyến kh&iacute;ch c&aacute;c bạn trẻ ở c&aacute;c tỉnh th&agrave;nh được tham gia một cuộc thi s&aacute;ng tạo vui tươi v&agrave; gi&uacute;p c&aacute;c bạn được tiếp cận lần đầu ti&ecirc;n với Arduino. BQT muốn tổ chức một cuộc thi d&agrave;nh ri&ecirc;ng cho c&aacute;c bạn để c&aacute;c bạn c&oacute; một m&ocirc;i trường mới để ph&aacute;t triển tương lai. V&agrave; tất cả những điều đ&oacute; đều hướng đến&nbsp;</span></span></span><a href=\"http://arduino.vn/ve-chung-toi\" target=\"_blank\"><span style=\"color:#8e44ad\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">mục đ&iacute;ch ph&aacute;t triển của cộng đồng</span></span></span></a><span style=\"color:#8e44ad\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:teal\">2. Đối tượng của cuộc thi</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c bạn học sinh c&oacute; ng&agrave;y sinh từ <strong>15/7/1990</strong> đến ng&agrave;y <strong>15/7/2006</strong>. C&oacute; thể dự thi theo nh&oacute;m tối đa 02 bạn. C&oacute; thể c&oacute; c&aacute;c anh chị hoặc người đi trước hướng dẫn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Khuyến kh&iacute;ch c&aacute;c anh, c&aacute;c chị đi trước c&oacute; kinh nghiệm về Arduino hỗ trợ cho em của m&igrave;nh ho&agrave;n thiện dự &aacute;n.</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:teal\">3. Nội dung v&agrave; h&igrave;nh thức thi</span></span></span></strong></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nội dung&nbsp;v&agrave; ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; sản phẩm dự thi</span></span></em></strong></span></span></span>\r\n\r\n	<ul style=\"list-style-type:circle\">\r\n		<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dựa tr&ecirc;n nền tảng board mạch Arduino được Cộng đồng Arduino Việt Nam cung cấp, th&iacute; sinh chọn đề t&agrave;i một c&aacute;ch độc lập để ph&aacute;t triển tạo ra sản phẩm ứng dụng. Sản phẩm tham gia Cuộc thi c&oacute; thể li&ecirc;n quan đến c&aacute;c chủ đề sau (nhưng kh&ocirc;ng hạn chế):&nbsp; </span></span></span></span></span>\r\n\r\n		<ul style=\"list-style-type:square\">\r\n			<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bảng đ&egrave;n led cho cộng đồng, điều khiển thiết bị, ứng dụng&hellip; </span></span></span></span></span></li>\r\n			<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Xe tự h&agrave;nh c&aacute;c loại ( c&oacute; thể c&oacute; sự kết hợp )</span></span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ti&ecirc;u ch&iacute; chung</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">: </span></span></span></span></span>\r\n		<ul style=\"list-style-type:square\">\r\n			<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c th&agrave;nh vi&ecirc;n trong Ban Tổ Chức của Cộng đồng (<strong>50% điểm</strong>)</span></span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vận dụng khả năng của mạch Arduino:</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> digital, analog, pwm, interrupt, bất đồng bộ,...</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Những &yacute; tưởng g&igrave; l&uacute;c đăng k&yacute; dự thi, v&agrave; bạn đ&atilde; l&agrave;m được bao nhi&ecirc;u phần trăm của dự &aacute;n.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&iacute;nh thực tiễn</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&iacute;nh mới</span></span></span></span></span>\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul style=\"list-style-type:square\">\r\n			<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&aacute;nh gi&aacute; từ Cộng đồng, c&ocirc;ng thức t&iacute;nh điểm (</span></span><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">50% điểm</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">):</span></span></span></span></span>\r\n			<ol>\r\n				<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tổng số điểm like của b&agrave;i viết giới thiệu về dự &aacute;n v&agrave; c&aacute;ch l&agrave;m dự &aacute;n của c&aacute;c bạn / tổng số điểm like của to&agrave;n bộ b&agrave;i viết của to&agrave;n bộ c&aacute;c nh&oacute;m tham dự.</span></span></span></span></span></li>\r\n			</ol>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li style=\"text-align:justify\" value=\"2\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&igrave;nh thức</span></span></em></strong></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cuộc thi được tổ chức th&agrave;nh&nbsp;<strong>01 v&ograve;ng</strong> v&agrave; theo thể thức như sau:</span></span></span></span></span>\r\n		<ul style=\"list-style-type:square\">\r\n			<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Th&ocirc;ng tin - &yacute; tưởng</span></span></em></strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">:&nbsp;</span></span></em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">c&aacute;c đội sẽ đăng k&yacute; &yacute; tưởng v&agrave;o form do BTC cung cấp</span></span></span></span></span></li>\r\n			<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">V&ograve;ng chung khảo</span></span></em></strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">:</span></span></em>&nbsp;<span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;C&aacute;c đội sẽ thực hiện dự &aacute;n v&agrave; nộp lại dự &aacute;n cho BTC.Sau đ&oacute;, BTC sẽ c&ocirc;ng bố dự &aacute;n của c&aacute;c bạn tại trang chủ ! Sau đ&oacute;, dựa v&agrave;o c&aacute;ch t&iacute;nh điểm n&ecirc;u tr&ecirc;n, ch&uacute;ng ta sẽ lựa ra 3 nh&oacute;m c&oacute; điểm số cao nhất từ đ&oacute; trao giải thưởng.</span></span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">4. Đăng k&yacute; dự thi v&agrave; c&aacute;c mốc thời gian</span></span></strong></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#8e44ad\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đăng k&yacute; dự thi tại&nbsp;<strong><a href=\"https://forms.gle/3UEY2R8FHyDtzoW98\">Đ&Acirc;Y</a>&nbsp;</strong>Form sẽ c&oacute; hiệu lực từ ng&agrave;y h&ocirc;m nay<strong> 29/04/2019</strong> đến hết ng&agrave;y&nbsp;<strong>5/7/2019</strong>.&nbsp;<strong>C&aacute;c bạn nhớ lưu giữ lại địa chỉ để chỉnh sửa c&acirc;u trả lời </strong></span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">BQT sẽ gửi mail th&ocirc;ng b&aacute;o kết quả cho từng bạn v&agrave; c&ocirc;ng bố kết quả ch&iacute;nh thức tr&ecirc;n trang chủ trước ng&agrave;y&nbsp;<strong>09/09/2016</strong>.&nbsp;<strong>Kết th&uacute;c cuộc thi</strong></span></span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lưu &yacute;</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">: điểm của BGK chỉ chiếm 50%, 50% c&ograve;n lại ph&ugrave; thuộc v&agrave;o việc bạn chia sẻ b&agrave;i viết v&agrave; số lượng y&ecirc;u th&iacute;ch của mọi người.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:teal\">5. Giải thưởng</span></span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">01 giải nhất: trị gi&aacute;&nbsp;</span></span></strong><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">150.000 đồng</span></span></strong></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">01 giải nh&igrave;: trị gi&aacute; 100.000 đồng</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">01 giải ba: trị gi&aacute; 50.000 đồng</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tiền thưởng sẽ được chuyển qua t&agrave;i khoản ng&acirc;n h&agrave;ng sau khi c&oacute; kết quả cho c&aacute;c nh&oacute;m.</span></span></em></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:teal\">6. Bản quyền</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bản quyền của c&aacute;c dự &aacute;n ho&agrave;n thiện sẽ thuộc về c&aacute;c th&iacute; sinh. Th&iacute; sinh phải tu&acirc;n theo c&aacute;c luật bản quyền v&agrave; kh&ocirc;ng vi phạm bản quyền của bất kỳ b&ecirc;n thứ ba n&agrave;o kh&aacute;c. Ch&uacute;ng t&ocirc;i kh&ocirc;ng chịu tr&aacute;ch nhiệm với bất kỳ h&igrave;nh thức vi phạm n&agrave;o của th&iacute; sinh.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:darkorange\">III. Th&ocirc;ng tin phụ lục</span></span></span></strong></span></span></p>\r\n\r\n<ol start=\"5\" style=\"list-style-type:lower-roman\">\r\n	<li style=\"text-align:justify\"><strong><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bộ dụng cụ cần thiết bao gồm:</span></span></span></span></span></strong>\r\n\r\n	<ul style=\"list-style-type:circle\">\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">01 Arduino Uno + d&acirc;y USB type B</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2 n&uacute;t bấm c&ocirc;ng nghiệp, 1 n&uacute;t bấm mạch loại to v&agrave; 1 n&uacute;t bấm mạch loại nhỏ.</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mini breadboard</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">25 sợi breadboard đực</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 đoạn d&acirc;y cứng</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 buzzer/module buzzer</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 mạch khuyếch đại &acirc;m thanh</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 cảm biến &aacute;nh s&aacute;ng</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 cảm biến si&ecirc;u &acirc;m srf04</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 cảm biến nhiệt độ LM35</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 cảm biến rung</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 HC595</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 LCD 16x02</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">15 led si&ecirc;u s&aacute;ng trắng, xanh dương v&agrave; đỏ</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">15 led 3mm</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">30 điện trở 560ohm.</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 biến trở 10k</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1 module relay</span></span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:darkorange\">IV. Th&ocirc;ng tin li&ecirc;n lạc</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mọi thắc mắc về cuộc thi xin c&aacute;c bạn vui l&ograve;ng li&ecirc;n hệ địa chỉ mail: arduinocombokydieu@gmail.com</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n','/media/BACKGROUND.png','2019-04-28 18:03:11.583902','2019-04-28 18:04:37.347985','2019-04-29 00:00:00.000000','2019-09-09 00:00:00.000000',11,0,1,'',5,3),(2,'Cùng Alice bảo vệ môi trường','Cùng Alice bảo vệ môi trường là cuộc thi được nhằm mục đích tạo ra sân chơi vui vẻ, lành mạnh cho các bạn trẻ, các em học sinh có niềm đam mê to lớn với lập trình.','<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#2980b9\"><u><strong>Giới thiệu:</strong></u></span> Như c&aacute;c bạn đ&atilde; biết, ng&agrave;y 5 th&aacute;ng 6 hằng năm được chọn l&agrave;m&nbsp;ng&agrave;y m&ocirc;i tường thế giới. V&igrave; vậy để hướng ứng ng&agrave;y n&agrave;y, ch&uacute;ng t&ocirc;i tổ chức cuộc thi C&ugrave;ng Alice bảo vệ m&ocirc;i trường.&nbsp;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#27ae60\"><u><strong>Đề t&agrave;i:</strong></u></span> H&atilde;y tạo một <strong>C&Acirc;U CHUYỆN BẰNG ALICE</strong>, nội dung&nbsp;c&oacute; li&ecirc;n quan đến bảo vệ m&ocirc;i trường.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#16a085\"><u><strong>C&aacute;ch thức đăng k&iacute;:</strong></u></span> C&aacute;c bạn đăng k&iacute; theo mẫu dưới đ&acirc;y (Hoặc truy cập v&agrave;o&nbsp;<a href=\"https://docs.google.com/forms/d/e/1FAIpQLSczNOF_vVjj-lMgW_0gcKwh-xaF0L7FGP26MR3XnzAEuBSqaQ/viewform?usp=sf_link\">Link</a>)</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><iframe frameborder=\"0\" height=\"1439\" src=\"https://docs.google.com/forms/d/e/1FAIpQLSczNOF_vVjj-lMgW_0gcKwh-xaF0L7FGP26MR3XnzAEuBSqaQ/viewform?embedded=true\" style=\"width:100%; height:500px\" width=\"640\">Đang tải...</iframe></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#8e44ad\"><u><strong>C&aacute;ch chấm điểm:</strong></u></span> B&agrave;i dự thi c&oacute; số lượt th&iacute;ch nhiều nhất sẽ chiến thắng.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong><span style=\"color:#e74c3c\">Giải thưởng:</span></strong></u><br />\r\n&nbsp; &nbsp; &nbsp;Giải nhất: Thẻ điện thoại Viettel 500.000<br />\r\n&nbsp; &nbsp; &nbsp;Giải nh&igrave;: Thẻ điện thoại Viettel 200.000<br />\r\n&nbsp; &nbsp; &nbsp;Giải ba: Thẻ điện thoại Viettel 100.000<br />\r\n&nbsp; &nbsp; &nbsp;3 giải khuyến kh&iacute;ch: Thẻ điện thoại Viettel 50.000<br />\r\n&nbsp; <span style=\"color:#e74c3c\"><u><em>C&aacute;ch thức trao giải:</em></u></span> Gửi Số seri, M&atilde; thẻ điện thoại qua Gmail</span></span></p>\r\n','/media/BackgroundAlice.jpg','2019-05-02 20:40:42.757787','2019-05-09 12:04:42.801131','2019-05-10 00:00:00.000000','2019-06-05 00:00:00.000000',13,0,0,'',6,2),(3,'Mùa hè cùng Alice','Mùa hẻ cùng Alice là cuộc thi được nhằm mục đích tạo ra sân chơi vui vẻ, lành mạnh cho các bạn trẻ, các em học sinh có niềm đam mê to lớn với lập trình.','<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#2980b9\"><u><strong>Giới thiệu:</strong></u></span> M&ugrave;a h&egrave; đến rồi, c&aacute;c em c&oacute; dự định g&igrave; chưa? Nếu chưa c&oacute; dự định g&igrave; cho m&ugrave;a h&egrave;, sao c&aacute;c em kh&ocirc;ng tham gia cuộc thi M&ugrave;a h&egrave; c&ugrave;ng Alice. Ở cuộc thi n&agrave;y c&aacute;c em c&oacute; thể thỏa sức s&aacute;ng tạo ra những tr&ograve; chơi, những c&acirc;u chuyện của ri&ecirc;ng m&igrave;nh v&agrave; chia sẻ những c&acirc;u chuyện, tr&ograve; chơi đến với mọi người&nbsp;mọi người.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#27ae60\"><u><strong>Đề t&agrave;i:</strong></u></span> Kh&ocirc;ng giới hạn nội dung</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#16a085\"><u><strong>C&aacute;ch thức đăng k&iacute;:</strong></u></span> C&aacute;c em đăng k&iacute; theo mẫu dưới đ&acirc;y (Hoặc truy cập v&agrave;o&nbsp;<a href=\"https://docs.google.com/forms/d/e/1FAIpQLSfL_RBBRIJxgr95n7w4P0kNqa5ua_SxxLhRVmakrxDBZdjydg/viewform?usp=sf_link\">Link</a>)</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><iframe style=\"width:100%;height:500px\" frameborder=\"0\" height=\"1641\" src=\"https://docs.google.com/forms/d/e/1FAIpQLSfL_RBBRIJxgr95n7w4P0kNqa5ua_SxxLhRVmakrxDBZdjydg/viewform?embedded=true\" width=\"640\">Đang tải...</iframe></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#8e44ad\"><u><strong>C&aacute;ch chấm điểm:</strong></u></span>&nbsp;B&agrave;i dự thi c&oacute; số lượt th&iacute;ch nhiều nhất sẽ chiến thắng.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong><span style=\"color:#e74c3c\">Giải thưởng</span>:</strong></u><br />\r\n&nbsp; &nbsp; &nbsp;Giải nhất: Thẻ điện thoại Viettel 500.000<br />\r\n&nbsp; &nbsp; &nbsp;Giải nh&igrave;: Thẻ điện thoại Viettel 200.000<br />\r\n&nbsp; &nbsp; &nbsp;Giải ba: Thẻ điện thoại Viettel 100.000<br />\r\n&nbsp; &nbsp; &nbsp;3 giải khuyến kh&iacute;ch: Thẻ điện thoại Viettel 50.000<br />\r\n&nbsp;&nbsp;<span style=\"color:#e74c3c\"><u><em>C&aacute;ch thức trao giải:</em></u></span>&nbsp;Gửi Số seri, M&atilde; thẻ điện thoại qua Gmail</span></span></p>\r\n','/media/Alice.in_.Wonderland.full_.1451133-901x605.jpg','2019-05-02 21:01:01.975018','2019-05-09 11:59:01.432661','2019-06-15 00:00:00.000000','2019-08-31 00:00:00.000000',13,0,0,'',6,2),(4,'TYNKER - LẬP TRÌNH TÍ HON','TYNKER - LẬP TRÌNH TÍ HON là cuộc thi được khởi động nhằm mục đích tạo ra sân chơi vui vẻ, lành mạnh cho các bạn trẻ, các em học sinh có niềm đam mê to lớn với lập trình game.','<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>I. Giới thiệu</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>TYNKER - LẬP TR&Igrave;NH T&Iacute; HON </strong>l&agrave; cuộc thi được khởi động nhằm mục&nbsp;đ&iacute;ch tạo ra s&acirc;n chơi vui vẻ, l&agrave;nh mạnh cho c&aacute;c bạn trẻ, c&aacute;c em học sinh c&oacute; niềm đam m&ecirc; to lớn với lập&nbsp;tr&igrave;nh. Thay v&igrave; tiếp cận với c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh phức tạp, c&aacute;c em sử dụng Tynker,&nbsp;<strong>một ng&ocirc;n ngữ lập tr&igrave;nh trực quan, học m&agrave; chơi, chơi m&agrave; học</strong>.&nbsp;Tham gia cuộc thi n&agrave;y, c&aacute;c bạn sẽ được giao lưu v&agrave; học hỏi kinh nghiệm từ nhiều bạn đến từ khắp mọi nơi tr&ecirc;n đất nước, những bạn c&oacute; c&ugrave;ng sở th&iacute;ch v&agrave; niềm đam m&ecirc;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">VS-Pro &ndash; với sứ mệnh l&agrave; đơn vị ti&ecirc;n phong gi&uacute;p h&agrave;ng triệu trẻ em Việt Nam ph&aacute;t triển tư duy to&agrave;n diện, năng động s&aacute;ng tạo, chủ động hơn trong cuộc sống, bắt kịp xu hướng thế giới !! VS-Pro tin rằng sẽ c&oacute; thật nhiều cuộc thi&nbsp;như thế &ndash; Tr&agrave;n ngập tiếng cười, say m&ecirc; v&agrave; nhiệt huyết !!!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/02/picture23.png\" style=\"height:307px; width:600px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>II. Thể lệ cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1. Mục đ&iacute;ch của cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\"><span style=\"color:#d35400\">Mang lại niềm y&ecirc;u th&iacute;ch l&agrave;nh mạnh đối với c&aacute;c hoạt động học tập v&agrave; giải tr&iacute; tr&ecirc;n một chiếc m&aacute;y t&iacute;nh. C&aacute;c em học được những nguy&ecirc;n l&yacute; cơ bản của việc thiết kế, học được c&aacute;ch thử nghiệm &yacute; tưởng mới,&nbsp;r&egrave;n luyện tư duy s&aacute;ng tạo với c&aacute;c sản phẩm tự tạo l&agrave; c&aacute;c đoạn phim ngắn hay tr&ograve; chơi vui nhộn tr&ecirc;n m&aacute;y t&iacute;nh. C&aacute;c em được c&aacute;ch hợp t&aacute;c với c&aacute;c bạn kh&aacute;c để thực hiện dự &aacute;n, học được c&aacute;ch t&igrave;m v&agrave; sửa lỗi khi kết quả kh&ocirc;ng được như &yacute;, tập được t&iacute;nh ki&ecirc;n tr&igrave; khi đối mặt với kh&oacute; khăn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2. Đối tượng của cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c bạn học sinh Tiểu học c&oacute; đam m&ecirc; với lập tr&igrave;nh game, v&agrave; muốn tự tay tạo ra c&aacute;c game m&agrave; m&igrave;nh mong muốn v&agrave; y&ecirc;u th&iacute;ch. C&oacute; thể dự thi theo nh&oacute;m tối đa 02 bạn. C&oacute; thể c&oacute; c&aacute;c anh chị hoặc người đi trước hướng dẫn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Khuyến kh&iacute;ch c&aacute;c anh, c&aacute;c chị đi trước c&oacute; kinh nghiệm về Tynker hỗ trợ cho em của m&igrave;nh ho&agrave;n thiện dự &aacute;n.</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3. Nội dung, ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; v&agrave; h&igrave;nh thức dự thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nội dung</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><em><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Viết chương tr&igrave;nh m&ocirc; tả chiếc xe &ocirc; t&ocirc; chuyển động tr&ecirc;n m&agrave;n h&igrave;nh, từ tr&aacute;i sang phải. Nếu gặp cạnh phải th&igrave; xuất hiện lại từ b&ecirc;n tr&aacute;i v&agrave; tiếp tục chạy</span></span></span></strong></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/02/picture17.png\" style=\"height:338px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>- Ghi ch&uacute;: </strong>Đ&acirc;y l&agrave; b&agrave;i to&aacute;n đơn giản tuy nhi&ecirc;n c&oacute; thể ph&aacute;t triển b&agrave;i to&aacute;n theo c&aacute;c mức độ kh&aacute;c nhau như sau:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>+ Mức độ 1:</em></strong> Chỉ y&ecirc;u cầu chiếc &ocirc; t&ocirc; di chuyển (kh&ocirc;ng lăn b&aacute;nh)</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>+ Mức độ 2: </em></strong>Y&ecirc;u cầu chiếc &ocirc; t&ocirc; di chuyển c&oacute; lăn b&aacute;nh nhưng th&acirc;n xe đứng y&ecirc;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>+ Mức độ 3: </em></strong>Tr&ecirc;n đường đi, đường kh&ocirc;ng &ecirc;m v&igrave; vậy xe di chuyển cả b&aacute;nh v&agrave; th&acirc;n xe c&oacute; độ x&oacute;c</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute;&nbsp;</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"color:#d35400\"><strong><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ C&aacute;c th&agrave;nh vi&ecirc;n trong Ban Tổ Chức của Cộng đồng (50% điểm)</span></span></strong></span></em></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Vận dụng kiến thức lập tr&igrave;nh về ng&ocirc;n ngữ lập tr&igrave;nh Tynker</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Những &yacute; tưởng g&igrave; l&uacute;c đăng k&yacute; dự thi, v&agrave; bạn đ&atilde; l&agrave;m được bao nhi&ecirc;u phần trăm của dự &aacute;n.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">T&iacute;nh thực tiễn</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">T&iacute;nh mới</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><strong><em><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Đ&aacute;nh gi&aacute; từ Cộng đồng, c&ocirc;ng thức t&iacute;nh điểm (50% điểm)</span></span></em></strong></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tổng số điểm like của b&agrave;i viết giới thiệu về dự &aacute;n v&agrave; c&aacute;ch l&agrave;m dự &aacute;n của c&aacute;c bạn / tổng số điểm like của to&agrave;n bộ b&agrave;i viết của to&agrave;n bộ c&aacute;c nh&oacute;m tham dự.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- H&igrave;nh thức dự thi</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Cuộc thi được tổ chức th&agrave;nh&nbsp;<strong>01 v&ograve;ng</strong>&nbsp;v&agrave; theo thể thức như sau:</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Th&ocirc;ng tin - &yacute; tưởng</em></strong><em>:&nbsp;</em>c&aacute;c đội sẽ đăng k&yacute; &yacute; tưởng v&agrave;o form do BTC cung cấp</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>V&ograve;ng chung khảo</em></strong><em>:</em>&nbsp;&nbsp;C&aacute;c đội sẽ thực hiện dự &aacute;n v&agrave; nộp lại dự &aacute;n cho BTC. Sau đ&oacute;, BTC sẽ c&ocirc;ng bố dự &aacute;n của c&aacute;c bạn tại trang chủ! Sau đ&oacute;, dựa v&agrave;o c&aacute;ch t&iacute;nh điểm n&ecirc;u tr&ecirc;n, ch&uacute;ng ta sẽ lựa ra 3 nh&oacute;m c&oacute; điểm số cao nhất từ đ&oacute; trao giải thưởng.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>4. Đăng k&yacute; dự thi v&agrave; c&aacute;c mốc thời gian</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#d35400\">- Đăng k&yacute; dự thi tại&nbsp;</span><strong><a href=\"https://forms.gle/jhX527zVbZy4smcy9\"><span style=\"color:#2980b9\">Đ&Acirc;Y</span></a><span style=\"color:#2980b9\">&nbsp;</span></strong><span style=\"color:#d35400\">Form sẽ c&oacute; hiệu lực từ ng&agrave;y h&ocirc;m nay<strong>&nbsp;02/05/2019</strong>&nbsp;đến hết ng&agrave;y <strong>05/07/2019</strong>.&nbsp;<strong>C&aacute;c bạn nhớ lưu giữ lại địa chỉ để chỉnh sửa c&acirc;u trả lời</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- BTC&nbsp;sẽ gửi mail th&ocirc;ng b&aacute;o kết quả cho từng bạn v&agrave; c&ocirc;ng bố kết quả ch&iacute;nh thức tr&ecirc;n trang chủ trước ng&agrave;y&nbsp;<strong>09/09/2019</strong>.&nbsp;<strong>Kết th&uacute;c cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Lưu &yacute;</strong>: điểm của BGK chỉ chiếm 50%, 50% c&ograve;n lại ph&ugrave; thuộc v&agrave;o việc bạn chia sẻ b&agrave;i viết v&agrave; số lượng y&ecirc;u th&iacute;ch của mọi người.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>5. Giải thưởng</strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><span style=\"color:#f39c12\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">01 giải nhất: trị gi&aacute;&nbsp;150.000 đồng</span></span></span></strong></li>\r\n	<li style=\"text-align:justify\"><strong><span style=\"color:#8e44ad\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">01 giải nh&igrave;: trị gi&aacute; 100.000 đồng</span></span></span></strong></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#27ae60\"><strong><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">01 giải ba: trị gi&aacute; 50.000 đồng</span></span></strong></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Tiền thưởng sẽ được chuyển qua t&agrave;i khoản ng&acirc;n h&agrave;ng sau khi c&oacute; kết quả cho c&aacute;c nh&oacute;m.</em></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>6. Bản quyền</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bản quyền của c&aacute;c dự &aacute;n ho&agrave;n thiện sẽ thuộc về c&aacute;c th&iacute; sinh. Th&iacute; sinh phải tu&acirc;n theo c&aacute;c luật bản quyền v&agrave; kh&ocirc;ng vi phạm bản quyền của bất kỳ b&ecirc;n thứ ba n&agrave;o kh&aacute;c. Ch&uacute;ng t&ocirc;i kh&ocirc;ng chịu tr&aacute;ch nhiệm với bất kỳ h&igrave;nh thức vi phạm n&agrave;o của th&iacute; sinh.</span></span></span></p>\r\n','/media/Picture23.png','2019-05-02 22:02:54.489402','2019-05-06 18:03:34.795198','2019-05-07 00:00:00.000000','2019-05-12 00:00:00.000000',11,0,0,'',7,1),(5,'SIÊU LẬP TRÌNH NHÍ SCRATCH','SIÊU LẬP TRÌNH NHÍ SCRATCH là cuộc thi được khởi động nhằm mục đích tạo ra sân chơi vui vẻ, lành mạnh cho các bạn trẻ, các em học sinh có niềm đam mê to lớn với lập trình game.','<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>I. Giới thiệu</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>SI&Ecirc;U LẬP TR&Igrave;NH NH&Iacute; SCRATCH&nbsp;</strong>l&agrave; cuộc thi được khởi động nhằm mục&nbsp;đ&iacute;ch tạo ra s&acirc;n chơi vui vẻ, l&agrave;nh mạnh cho c&aacute;c bạn trẻ, c&aacute;c em học sinh c&oacute; niềm đam m&ecirc; to lớn với lập&nbsp;tr&igrave;nh. Thay v&igrave; tiếp cận với c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh phức tạp, c&aacute;c em sử dụng Scratch,&nbsp;<strong>một ng&ocirc;n ngữ lập tr&igrave;nh trực quan, học m&agrave; chơi, chơi m&agrave; học</strong>.&nbsp;Tham gia cuộc thi n&agrave;y, c&aacute;c bạn sẽ được giao lưu v&agrave; học hỏi kinh nghiệm từ nhiều bạn đến từ khắp mọi nơi tr&ecirc;n đất nước, những bạn c&oacute; c&ugrave;ng sở th&iacute;ch v&agrave; niềm đam m&ecirc;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">VS-Pro &ndash; với sứ mệnh l&agrave; đơn vị ti&ecirc;n phong gi&uacute;p h&agrave;ng triệu trẻ em Việt Nam ph&aacute;t triển tư duy to&agrave;n diện, năng động s&aacute;ng tạo, chủ động hơn trong cuộc sống, bắt kịp xu hướng thế giới !! VS-Pro tin rằng sẽ c&oacute; thật nhiều cuộc thi&nbsp;như thế &ndash; Tr&agrave;n ngập tiếng cười, say m&ecirc; v&agrave; nhiệt huyết !!!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/02/picture50.png\" style=\"height:315px; width:600px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>II. Thể lệ cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1. Mục đ&iacute;ch của cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mang lại niềm y&ecirc;u th&iacute;ch l&agrave;nh mạnh đối với c&aacute;c hoạt động học tập v&agrave; giải tr&iacute; tr&ecirc;n một chiếc m&aacute;y t&iacute;nh. C&aacute;c em học được những nguy&ecirc;n l&yacute; cơ bản của việc thiết kế, học được c&aacute;ch thử nghiệm &yacute; tưởng mới,&nbsp;r&egrave;n luyện tư duy s&aacute;ng tạo với c&aacute;c sản phẩm tự tạo l&agrave; c&aacute;c đoạn phim ngắn hay tr&ograve; chơi vui nhộn tr&ecirc;n m&aacute;y t&iacute;nh. C&aacute;c em được c&aacute;ch hợp t&aacute;c với c&aacute;c bạn kh&aacute;c để thực hiện dự &aacute;n, học được c&aacute;ch t&igrave;m v&agrave; sửa lỗi khi kết quả kh&ocirc;ng được như &yacute;, tập được t&iacute;nh ki&ecirc;n tr&igrave; khi đối mặt với kh&oacute; khăn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2. Đối tượng của cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c bạn học sinh Tiểu học c&oacute; đam m&ecirc; với lập tr&igrave;nh game, v&agrave; muốn tự tay tạo ra c&aacute;c game m&agrave; m&igrave;nh mong muốn v&agrave; y&ecirc;u th&iacute;ch. C&oacute; thể dự thi theo nh&oacute;m tối đa 02 bạn. C&oacute; thể c&oacute; c&aacute;c anh chị hoặc người đi trước hướng dẫn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Khuyến kh&iacute;ch c&aacute;c anh, c&aacute;c chị đi trước c&oacute; kinh nghiệm về Scratch hỗ trợ cho em của m&igrave;nh ho&agrave;n thiện dự &aacute;n.</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3. Nội dung, ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; v&agrave; h&igrave;nh thức dự thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>- Nội dung</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Viết chương tr&igrave;nh Scratch m&ocirc; tả đ&aacute;nh b&oacute;ng kiểu tr&ograve; chơi Bi-a như sau:</strong></span></span></span></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/02/capture.PNG\" style=\"height:355px; width:484px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">M&agrave;n h&igrave;nh ban đầu của chương tr&igrave;nh như h&igrave;nh b&ecirc;n. Nh&acirc;n vật ch&iacute;nh của chương tr&igrave;nh l&agrave; quả b&oacute;ng. Tr&ecirc;n m&agrave;n h&igrave;nh thể hiện 1 h&igrave;nh chữ nhật m&agrave;u đỏ</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi nh&aacute;y chuột quả b&oacute;ng bắt đầu chuyển động với vận tốc trung b&igrave;nh. Khi gặp cạnh h&igrave;nh chữ nhật b&oacute;ng sẽ bật lại theo hướng đ&uacute;ng nguy&ecirc;n tắc của tr&ograve; chơi bi-a.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/02/capture1.PNG\" style=\"height:363px; width:508px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bất cứ l&uacute;c n&agrave;o c&aacute;c em nh&aacute;y chuột sẽ l&agrave;m đổi hướng b&oacute;ng: b&oacute;ng sẽ chuyển động về hướng con trỏ chuột. Khi gặp cạnh sẽ c&oacute; 1 tiếng k&ecirc;u nhỏ ph&aacute;t ra.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c em h&atilde;y viết chương tr&igrave;nh m&ocirc; tả y&ecirc;u cầu tr&ecirc;n.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ch&uacute; &yacute;: Khung h&igrave;nh chữ nhật đỏ c&oacute; thể l&agrave; h&igrave;nh ảnh nền s&acirc;n khấu, c&oacute; thể l&agrave; 1 nh&acirc;n vật Scratch, c&oacute; thể l&agrave; h&igrave;nh vẽ do ch&iacute;nh quả b&oacute;ng vẽ ra khi chạy chương tr&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>- Ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute;&nbsp;</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><strong>+ C&aacute;c th&agrave;nh vi&ecirc;n trong Ban Tổ Chức của Cộng đồng (50% điểm)</strong></em></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Vận dụng kiến thức lập tr&igrave;nh về ng&ocirc;n ngữ lập tr&igrave;nh Tynker</span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Những &yacute; tưởng g&igrave; l&uacute;c đăng k&yacute; dự thi, v&agrave; bạn đ&atilde; l&agrave;m được bao nhi&ecirc;u phần trăm của dự &aacute;n.</span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">T&iacute;nh thực tiễn</span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">T&iacute;nh mới</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>+ Đ&aacute;nh gi&aacute; từ Cộng đồng, c&ocirc;ng thức t&iacute;nh điểm (50% điểm)</em></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tổng số điểm like của b&agrave;i viết giới thiệu về dự &aacute;n v&agrave; c&aacute;ch l&agrave;m dự &aacute;n của c&aacute;c bạn / tổng số điểm like của to&agrave;n bộ b&agrave;i viết của to&agrave;n bộ c&aacute;c nh&oacute;m tham dự.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>- H&igrave;nh thức dự thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Cuộc thi được tổ chức th&agrave;nh&nbsp;<strong>01 v&ograve;ng</strong>&nbsp;v&agrave; theo thể thức như sau:</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Th&ocirc;ng tin - &yacute; tưởng</em></strong><em>:&nbsp;</em>c&aacute;c đội sẽ đăng k&yacute; &yacute; tưởng v&agrave;o form do BTC cung cấp</span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>V&ograve;ng chung khảo</em></strong><em>:</em>&nbsp;&nbsp;C&aacute;c đội sẽ thực hiện dự &aacute;n v&agrave; nộp lại dự &aacute;n cho BTC. Sau đ&oacute;, BTC sẽ c&ocirc;ng bố dự &aacute;n của c&aacute;c bạn tại trang chủ! Sau đ&oacute;, dựa v&agrave;o c&aacute;ch t&iacute;nh điểm n&ecirc;u tr&ecirc;n, ch&uacute;ng ta sẽ lựa ra 3 nh&oacute;m c&oacute; điểm số cao nhất từ đ&oacute; trao giải thưởng.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>4. Đăng k&yacute; dự thi v&agrave; c&aacute;c mốc thời gian</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#d35400\">- Đăng k&yacute; dự thi tại&nbsp;</span><strong><a href=\"https://forms.gle/jhX527zVbZy4smcy9\"><span style=\"color:#2980b9\">Đ&Acirc;Y</span></a><span style=\"color:#d35400\">&nbsp;</span></strong><span style=\"color:#d35400\">Form sẽ c&oacute; hiệu lực từ ng&agrave;y h&ocirc;m nay<strong>&nbsp;02/05/2019</strong>&nbsp;đến hết ng&agrave;y 0<strong>5/07/2019</strong>.&nbsp;<strong>C&aacute;c bạn nhớ lưu giữ lại địa chỉ để chỉnh sửa c&acirc;u trả lời</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- BTC&nbsp;sẽ gửi mail th&ocirc;ng b&aacute;o kết quả cho từng bạn v&agrave; c&ocirc;ng bố kết quả ch&iacute;nh thức tr&ecirc;n trang chủ trước ng&agrave;y&nbsp;<strong>09/09/2019</strong>.&nbsp;<strong>Kết th&uacute;c cuộc thi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Lưu &yacute;</strong>: điểm của BGK chỉ chiếm 50%, 50% c&ograve;n lại ph&ugrave; thuộc v&agrave;o việc bạn chia sẻ b&agrave;i viết v&agrave; số lượng y&ecirc;u th&iacute;ch của mọi người.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>5. Giải thưởng</strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>01 giải nhất: trị gi&aacute;&nbsp;150.000 đồng</strong></span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#2980b9\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>01 giải nh&igrave;: trị gi&aacute; 100.000 đồng</strong></span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"color:#f39c12\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>01 giải ba: trị gi&aacute; 50.000 đồng</strong></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Tiền thưởng sẽ được chuyển qua t&agrave;i khoản ng&acirc;n h&agrave;ng sau khi c&oacute; kết quả cho c&aacute;c nh&oacute;m.</em></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>6. Bản quyền</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bản quyền của c&aacute;c dự &aacute;n ho&agrave;n thiện sẽ thuộc về c&aacute;c th&iacute; sinh. Th&iacute; sinh phải tu&acirc;n theo c&aacute;c luật bản quyền v&agrave; kh&ocirc;ng vi phạm bản quyền của bất kỳ b&ecirc;n thứ ba n&agrave;o kh&aacute;c. Ch&uacute;ng t&ocirc;i kh&ocirc;ng chịu tr&aacute;ch nhiệm với bất kỳ h&igrave;nh thức vi phạm n&agrave;o của th&iacute; sinh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n','/media/Picture50.png','2019-05-02 23:55:30.641328','2019-05-02 23:55:30.641335','2019-05-03 00:00:00.000000','2019-07-05 00:00:00.000000',4,0,0,'',7,5),(6,'Vietnam Maker Contest with Intel Galileo ','Cuộc thi Sáng tạo với Intel Edison lần 2 dành cho sinh viên sẽ được chính thức khởi động. ','<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Điều 1. Mục đ&iacute;ch của cuộc thi</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cuộc thi Nh&agrave; s&aacute;ng tạo Việt Nam với Intel Edison (Vietnam Maker Contest with Intel Edison - VMIG) là cu&ocirc;̣c thi s&aacute;ng tạo đ&acirc;̀u ti&ecirc;n tại Vi&ecirc;̣t Nam với quy m&ocirc; to&agrave;n quốc theo xu hướng sáng tạo Maker chung của th&ecirc;́ giới. Cuộc thi sử dụng bo mạch Intel&reg; Edison - bo mạch vi điều khiển đầu ti&ecirc;n được Intel nghi&ecirc;n cứu v&agrave; ph&aacute;t triển d&agrave;nh ri&ecirc;ng cho ng&agrave;nh gi&aacute;o dục - nhằm hỗ trợ c&aacute;c nh&agrave; gi&aacute;o dục, học sinh v&agrave; những người đam m&ecirc; s&aacute;ng tạo biến c&aacute;c &yacute; tưởng th&agrave;nh sản phẩm một c&aacute;ch dễ d&agrave;ng.</span></span></span></span></em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mục đ&iacute;ch của Cuộc thi nhằm khuyến kh&iacute;ch tinh thần s&aacute;ng tạo, nghi&ecirc;n cứu khoa học của sinh vi&ecirc;n, đẩy mạnh việc thực h&agrave;nh, ứng dụng trong giảng dạy, gi&uacute;p x&acirc;y dựng nguồn nh&acirc;n lực kỹ thuật ch&acirc;́t lượng cao đ&aacute;p ứng nhu cầu ph&aacute;t triển c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp c&ocirc;ng nghệ cao của Việt Nam.</span></span></span></span></em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Điều 2. Đối tượng dự thi</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tất cả sinh vi&ecirc;n c&oacute; quốc tịch Việt Nam đang học tập tại c&aacute;c trường đại học, học viện tại Việt Nam.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c bạn sinh vi&ecirc;n học c&ugrave;ng trường, học viện c&oacute; thể tham dự theo đội, mỗi đội c&oacute; tối đa 03 sinh vi&ecirc;n (từ 01 đến 03 sinh vi&ecirc;n) v&agrave; 01 giảng vi&ecirc;n hỗ trợ.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Điều 3. Nội dung v&agrave; h&igrave;nh thức thi</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nội dung v&agrave; ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; sản phẩm dự thi</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dựa tr&ecirc;n nền tảng bo mạch Edison được Intel cung cấp, th&iacute; sinh chọn đề t&agrave;i một c&aacute;ch độc lập để ph&aacute;t triển tạo ra sản phẩm ứng dụng. Sản phẩm tham gia Cuộc thi c&oacute; thể li&ecirc;n quan đến c&aacute;c chủ đề sau: giao th&ocirc;ng, y tế, m&ocirc;i trường.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><strong><span style=\"color:#27ae60\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ti&ecirc;u ch&iacute; chung để đ&aacute;nh gi&aacute; sản phẩm dự thi:</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sử dụng c&aacute;c nguồn lực hiệu quả. (bạn c&oacute; d&ugrave;ng hết khả năng của Edison hay kh&ocirc;ng?)</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&iacute;nh nguy&ecirc;n bản của dự &aacute;n thiết kế.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quy m&ocirc; v&agrave; t&iacute;nh phức tạp của sản phẩm.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kế hoạch v&agrave; kết quả thử nghiệm sản phẩm.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chất lượng v&agrave; tr&igrave;nh b&agrave;y của b&aacute;o c&aacute;o.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Phong c&aacute;ch tr&igrave;nh b&agrave;y b&aacute;o c&aacute;o với Ban Gi&aacute;m khảo.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><strong><span style=\"color:#16a085\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&igrave;nh thức</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cuộc thi được tổ chức th&agrave;nh 03 v&ograve;ng:</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">V&ograve;ng thi &yacute; tưởng: Th&iacute; sinh đăng k&yacute; tham dự chuẩn bị &yacute; tưởng v&agrave; nộp cho Ban Tổ chức Cuộc thi. Ban Gi&aacute;m khảo chấm, chọn tối đa 40 &yacute; tưởng xuất sắc nhất v&agrave;o v&ograve;ng sơ khảo.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">V&ograve;ng sơ khảo: Ban Gi&aacute;m khảo sẽ chấm c&aacute;c sản phẩm dự thi gửi về Ban Tổ chức để chọn tối đa 15 sản phẩm xuất sắc nhất tham gia v&ograve;ng chung khảo.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">V&ograve;ng chung khảo: C&aacute;c sản phẩm tham gia v&ograve;ng chung khảo được triển l&atilde;m, chấm trực tiếp v&agrave; chọn tối đa 10 sản phẩm xuất sắc nhất nhận giải thưởng của Cuộc thi. </span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Điều 4. Đăng k&yacute; dự thi</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">M&igrave;nh t&oacute;m lược chỉ ghi phần d&agrave;nh cho sinh vi&ecirc;n th&ocirc;i nh&eacute;.</span></span></span></span></em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đăng k&yacute; &yacute; tưởng dự thi (xem quy tr&igrave;nh) v&agrave; nộp (bản in v&agrave; file) tại Văn ph&ograve;ng Đo&agrave;n &ndash; Hội Sinh vi&ecirc;n của trường, học viện đang theo học trước ng&agrave;y 10/07/2019.</span></span></span></span></em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dự &aacute;n m&agrave; bạn đăng k&yacute; sẽ được duyệt, nếu &yacute; tưởng dự thi được v&agrave;o v&ograve;ng sơ khảo, th&iacute; sinh nộp cho Ban Tổ chức (bản in v&agrave; file) phiếu đăng k&yacute; dự thi, bản thuyết minh dự &aacute;n (theo mẫu) c&ugrave;ng h&igrave;nh ảnh, video clip giới thiệu về dự &aacute;n v&agrave; 02 ảnh m&agrave;u 4x6 (chụp năm 2019 c&oacute; ghi r&otilde; th&ocirc;ng tin ở mặt sau) trước ng&agrave;y 15/11/2019.</span></span></span></span></em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#16a085\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Điều 5. Thời gian, địa điểm tổ chức thi</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đăng k&yacute; dự thi v&agrave; nộp thuyết minh &yacute; tưởng s&aacute;ng tạo trước ng&agrave;y 10/07/2019</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nhận bo mạch Intel Edison Gen 2 v&agrave; 2 triệu đồng để triển khai dự &aacute;n xong trước ng&agrave;y 30/07/2019 v&agrave; bổ sung hồ sơ dự thi c&ugrave;ng b&aacute;o c&aacute;o về dự &aacute;n trước ng&agrave;y 15/11/2019.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chấm sơ khảo tại c&aacute;c địa điểm: H&agrave; Nội, TP. Hồ Ch&iacute; Minh, Đ&agrave; Nẵng dự kiến cuối th&aacute;ng 11/2019.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><em><span style=\"color:#8e44ad\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chấm chung khảo v&agrave; trao giải dự kiến tại Thủ đ&ocirc; H&agrave; Nội trong 02 ng&agrave;y 8-9/01/2019.</span></span></span></span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><strong><span style=\"color:#27ae60\"><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:16px\">Điều 6: Chi tiết li&ecirc;n hệ qua địa chỉ email: </span></span></span></strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:16px\"><em>arduinocombokydieu@gmail.com</em></span></span></span></p>\r\n','/media/2176_882450-1457658848-0-ko-mau-arduino2016-logotransparent.png','2019-05-09 11:20:15.562656','2019-05-09 11:28:14.253527','2019-05-09 00:00:00.000000','2019-11-22 00:00:00.000000',2,0,1,'',5,3);
