INSERT INTO `competitionsubmittion` VALUES (1,'2019-05-06 16:23:33.319858','2019-05-06 16:29:12.973541','https://www.tynker.com/ide/?p=5ca09263cebfbd55486b7344','Thi Nhanh Đáp Lẹ','<p>Sản phẩm của em c&oacute; &yacute; tưởng từ cuộc đua thi t&agrave;i tr&ecirc;n trang Y8</p>\r\n\r\n<p>&nbsp; &nbsp; Sản phẩm gồm c&oacute; 2 nh&acirc;n vật đua với nhau, điều khiển:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; - Người Chơi Thứ nhất : W,S,A,D</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;- Người Chơi Thứ hai : C&aacute;c ph&iacute;m mũi t&ecirc;n</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; Mục ti&ecirc;u l&agrave; đi đến l&agrave; vạch đ&iacute;ch cuối đường đua.</p>\r\n\r\n<p>&nbsp;</p>\r\n',1,'',33,4),(3,'2019-05-06 17:05:48.513974','2019-05-06 17:05:48.513984','http://vspro.ttth.hcmue.edu.vn/competition/4/detail','Siêu Xe Tốc Độ','<ul>\n	<li>Vận dụng kiến thức lập tr&igrave;nh về ng&ocirc;n ngữ lập tr&igrave;nh Tynker</li>\n	<li>Những &yacute; tưởng g&igrave; l&uacute;c đăng k&yacute; dự thi, v&agrave; bạn đ&atilde; l&agrave;m được bao nhi&ecirc;u phần trăm của dự &aacute;n.</li>\n	<li>T&iacute;nh thực tiễn</li>\n	<li>T&iacute;nh mới</li>\n</ul>\n',1,'',4,4),(4,'2019-05-06 17:47:43.660546','2019-05-06 17:47:43.660552','https://muaherucro.cuocthi.vn','Alice3D Mùa hè rực rỡ','<p>Chứa project Alice3D m&ugrave;a h&egrave; rực rỡ</p>\n',1,'',3,3),(5,'2019-05-08 23:28:11.557883','2019-05-08 23:28:11.557889','','LĂN BÁNH BẠN ƠI','<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Chương tr&igrave;nh m&ocirc; tả chiếc xe &ocirc; t&ocirc; chuyển động tr&ecirc;n m&agrave;n h&igrave;nh, từ tr&aacute;i sang phải. Nếu gặp cạnh phải th&igrave; xuất hiện lại từ b&ecirc;n tr&aacute;i v&agrave; tiếp tục chạy</strong></span></span></p>\n',1,'',37,4),(6,'2019-05-09 02:18:15.187123','2019-05-09 02:18:15.187128','','BI DA SIÊU SAO','<p>TRẦN THI&Ecirc;N HẢI_0123456789_BI DA SI&Ecirc;U SAO</p>\n',1,'',37,5),(7,'2019-05-09 02:23:29.903170'