INSERT INTO `forum` VALUES (1,'[Hướng Dẫn] Tạo Vòng Trái Tim Chớp Tắt Liên Tục Bằng Led Đơn Giản',NULL,'<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"color:#8e44ad\"><span style=\"font-size:20px\"><em><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o c&aacute;c anh chị v&agrave; c&aacute;c bạn, Em đang tham gia kh&oacute;a học về arduino cơ bản của website v&agrave; hiện đ&atilde; học đến b&agrave;i V&iacute; dụ đơn giản với Led. Tuy đ&atilde; cố gắng nhưng em mới chỉ c&oacute; thể l&agrave;m được những v&iacute; dụ đơn giản. B&acirc;y giờ em muốn l&agrave;m một c&aacute;i g&igrave; đ&oacute; hay ho hơn dữa tr&ecirc;n những c&aacute;i đ&atilde; học, Anh Chị n&agrave;o c&oacute; thể gi&uacute;p em tạo ra một tr&aacute;i tim v&ograve;ng bằng Led s&aacute;ng tắt li&ecirc;n tục được kh&ocirc;ng ạ ? Em ch&acirc;n th&agrave;nh cảm ơn anh chị.</span></span></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><img src=\"/media/2019/04/28/image_CJ9E4nx.png\" style=\"height:397px; width:530px\" /></p>\r\n','/media/dss.png','2019-04-28 17:32:10.568920','2019-04-28 17:32:10.568926',6,0,1,'',5,3,3),(2,'[Hỏi – Đáp] Nối Mạch Trong Động Cơ V1 ',NULL,'<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><strong><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&agrave;o anh chị v&agrave; c&aacute;c bạn, đang học l&agrave;m xe tự h&agrave;nh đơn giản , hiện tại em đ&atilde; học đến b&agrave;i cảm biến si&ecirc;u &acirc;m&hellip;em thấy nhiều anh chị d&ugrave;ng mỏ h&agrave;ng để kết nối mạch của động cơ với d&acirc;y dẫn. Nhưng bản th&acirc;n em lại kh&ocirc;ng c&oacute; mỏ h&agrave;ng v&agrave; cũng kh&ocirc;ng phải d&acirc;n chuy&ecirc;n về cơ kh&iacute; n&ecirc;n việc sử dụng mỏ h&agrave;ng đối với em l&agrave; rất nguy hiểm. V&igrave; thế, cho em hỏi l&agrave; ch&uacute;ng ta c&oacute; phương ph&aacute;p n&agrave;o kh&aacute;c để kết nối c&aacute;c linh kiện v&oacute;i nhau m&agrave; kh&ocirc;ng cần mỏ h&agrave;ng kh&ocirc;ng ạ ? </span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><strong><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/media/2019/04/28/47a1263d3ae09567ab11fb1c99da3594.jpg\" style=\"height:500px; width:500px\" /></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><strong><span style=\"color:#16a085\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Em xin ch&acirc;n th&agrave;nh cảm ơn!</span></span></span></span></strong></p>\r\n','/media/47a1263d3ae09567ab11fb1c99da3594.jpg','2019-04-28 17:35:19.771813','2019-04-28 17:35:19.771828',7,0,1,'',5,3,3),(3,'[ HỎI ĐÁP] Làm hệ thống tưới tiêu tự động ','Cần hướng dẫn Hệ thống tưới tiêu tự động  ','<p style=\"text-align:justify\"><span style=\"font-size:20px\"><strong><span style=\"color:#8e44ad\">Trong cuộc sống bận rộn hiện nay, nhiều bạn vẫn c&oacute; th&uacute; vui l&agrave; trồng những c&acirc;y cảnh, vườn rau trong kh&ocirc;ng gian trống của nh&agrave; m&igrave;nh như s&acirc;n thượng, ban c&ocirc;ng. Tuy nhi&ecirc;n, trong những l&uacute;c bạn bận đi c&ocirc;ng t&aacute;c nhiều ng&agrave;y th&igrave; những c&acirc;y cảnh v&agrave; vườn hoa ở nh&agrave; sẽ kh&ocirc;ng ai tưới nước.&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:20px\"><strong><span style=\"color:#8e44ad\"><img alt=\"\" src=\"/media/2019/04/28/he-thong-bom-nuoc-tu-dong-h4.jpg\" style=\"height:438px; width:594px\" /></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><strong><span style=\"color:#8e44ad\">V&igrave; vậy, h&ocirc;m nay m&igrave;nh tạo chủ đề n&agrave;y hy vọng c&oacute; cao nh&acirc;n gi&uacute;p đỡ... Hướng dẫn m&igrave;nh l&agrave;m hệ thống tưới ti&ecirc;u đơn giản bằng c&aacute;ch ứng dụng Mạch Arduino, &iacute;t tốn k&eacute;m x&iacute;u.</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><strong><span style=\"color:#8e44ad\">M&igrave;nh hy vọng nhận được nhiều phản hồi từ c&aacute;c bạn,&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><strong><span style=\"color:#8e44ad\">Tr&acirc;n trọng cảm ơn</span></strong></span></p>\r\n','/media/he-thong-bom-nuoc-tu-dong-h4.jpg','2019-04-28 18:14:55.160802','2019-04-28 18:14:55.160808',3,0,1,'',5,3,NULL),(4,'Tại sao khi chạy chương trình thời gian bằng 0 ngay lập tức',NULL,'<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">T&ocirc;i đ&atilde; tạo một thủ tục t&ecirc;n <strong>tinhthoigian</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/02/code1.JPG\" style=\"height:185px; width:460px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">T&ocirc;i đ&atilde; sử dụng sự kiện <strong>SceneActivation</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/02/code2.JPG\" style=\"height:124px; width:346px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&acirc;y l&agrave; kết quả khi t&ocirc;i chạy chương tr&igrave;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/02/chayct.JPG\" style=\"height:430px; width:655px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mọi người gi&uacute;p t&ocirc;i giải quyết lỗi n&agrave;y với, cảm ơn mọi người!</span></span></p>\r\n','/media/BackgroundAlice_hy70zfD.jpg','2019-05-02 21:24:16.553900','2019-05-02 21:24:16.553904',12,0,1,'',6,2,2),(6,'[Hỏi - Đáp] Ý tưởng tạo dự án \"Đố bạn, con vật gì đây?\"','1','<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/07/capture4.png\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Ch&agrave;o anh chị v&agrave; c&aacute;c bạn, dựa tr&ecirc;n &yacute; tưởng b&agrave;i &quot;Đi t&igrave;m ẩn số&quot;, em muốn thực hiện một dự &aacute;n tương tự. Cụ thể l&agrave; dự &aacute;n mang t&ecirc;n &quot;Đố bạn, con vật g&igrave; đ&acirc;y?&quot;. Vậy để thực hiện dự &aacute;n tr&ecirc;n, mọi người c&oacute; thể gợi &yacute; cho em &yacute; tưởng v&agrave; c&aacute;ch thực hiện như thế n&agrave;o được kh&ocirc;ng?</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#2980b9\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Em cảm ơn mọi người rất nhiều!</strong></span></span></span></p>\r\n','/media/Capture4.png','2019-05-07 12:15:05.213386','2019-05-07 16:08:31.976664',5,0,1,'',7,1,NULL),(7,'(P1) CÁCH GIẢI THÍCH THUẬT TOÁN CHO TRẺ EM','None','<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/07/algorithm-blog.png\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:#e74c3c\"><span style=\"font-size:28px\"><span style=\"font-family:Times New Roman,Times,serif\">C&Aacute;CH GIẢI TH&Iacute;CH THUẬT TO&Aacute;N CHO TRẺ EM</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\">Từ thuật to&aacute;n thuật ngữ c&oacute; thể kh&ocirc;ng li&ecirc;n quan đến trẻ em, nhưng sự thật l&agrave; c&aacute;c thuật to&aacute;n ở xung quanh ch&uacute;ng, chi phối mọi thứ từ c&ocirc;ng nghệ m&agrave; ch&uacute;ng sử dụng cho đến c&aacute;c quyết định trần tục m&agrave; ch&uacute;ng đưa ra mỗi ng&agrave;y. C&aacute;c thuật to&aacute;n rất hấp dẫn v&agrave;, mặc d&ugrave; một số kh&aacute; phức tạp, bản th&acirc;n kh&aacute;i niệm n&agrave;y thực sự kh&aacute; đơn giản.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\"><strong>Thuật to&aacute;n l&agrave; g&igrave;?</strong><br />\r\nThuật to&aacute;n l&agrave; một tập lệnh hoặc c&ocirc;ng thức từng bước chi tiết để giải quyết vấn đề hoặc ho&agrave;n th&agrave;nh một nhiệm vụ. Trong điện to&aacute;n, c&aacute;c lập tr&igrave;nh vi&ecirc;n viết c&aacute;c thuật to&aacute;n hướng dẫn m&aacute;y t&iacute;nh c&aacute;ch thực hiện một t&aacute;c vụ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\">Khi bạn nghĩ về một thuật to&aacute;n theo c&aacute;ch chung nhất (kh&ocirc;ng chỉ li&ecirc;n quan đến điện to&aacute;n), c&aacute;c thuật to&aacute;n c&oacute; ở khắp mọi nơi. Một c&ocirc;ng thức để l&agrave;m thức ăn l&agrave; một thuật to&aacute;n, phương ph&aacute;p bạn sử dụng để giải c&aacute;c b&agrave;i to&aacute;n cộng hoặc chia d&agrave;i l&agrave; một thuật to&aacute;n v&agrave; qu&aacute; tr&igrave;nh gấp &aacute;o hoặc một chiếc quần l&agrave; một thuật to&aacute;n. Ngay cả th&oacute;i quen buổi s&aacute;ng của bạn c&oacute; thể được coi l&agrave; một thuật to&aacute;n! Tr&ecirc;n thực tế, ở đ&acirc;y, những g&igrave; m&agrave; buổi s&aacute;ng con bạn c&oacute; thể tr&ocirc;ng giống như được viết ra như một thuật to&aacute;n:</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/07/2016-03-18.png\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Trẻ em c&oacute; thể viết c&aacute;c thuật to&aacute;n ri&ecirc;ng của họ!</strong><br />\r\nKhuyến kh&iacute;ch con bạn viết ra thuật to&aacute;n buổi s&aacute;ng của ch&uacute;ng, hoặc thuật to&aacute;n cho một nhiệm vụ thậm ch&iacute; đơn giản hơn, như đ&aacute;nh răng hoặc ăn ngũ cốc. Kh&ocirc;ng biết điều đ&oacute;, họ sẽ kh&aacute;m ph&aacute; c&aacute;c kh&aacute;i niệm t&iacute;nh to&aacute;n quan trọng như lặp đi lặp lại (chải răng tr&aacute;i năm lần), giải tr&igrave;nh tự (cho ngũ cốc v&agrave;o b&aacute;t v&agrave; sau đ&oacute; cho v&agrave;o sữa), v&agrave; logic c&oacute; điều kiện (nếu b&aacute;t rỗng, h&atilde;y ngừng ăn).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Th&aacute;ch thức con bạn c&agrave;ng cụ thể với c&aacute;c hướng dẫn c&agrave;ng tốt. M&aacute;y vi t&iacute;nh donith hiểu &yacute; định của bạn, v&igrave; vậy nếu bạn don quy định rằng bạn cần lấy b&aacute;t ra trước, cuối c&ugrave;ng bạn sẽ đổ sữa xuống s&agrave;n!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong lớp to&aacute;n, trẻ học về c&aacute;c số nguy&ecirc;n tố v&agrave; c&aacute;ch x&aacute;c định xem một số c&oacute; phải l&agrave; số nguy&ecirc;n tố hay kh&ocirc;ng. Nhưng với số lượng lớn, điều n&agrave;y rất kh&oacute; khăn! Đối với số 493, bạn phải thử hơn 15 ph&eacute;p t&iacute;nh để biết rằng 493 kh&ocirc;ng phải l&agrave; số nguy&ecirc;n tố (17 * 29 = 493). Trẻ em c&oacute; thể viết một thuật to&aacute;n trong Tynker để x&aacute;c định xem một số c&oacute; phải l&agrave; số nguy&ecirc;n tố hay kh&ocirc;ng.</span></span></span></p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"//www.tynker.com/ide/embedded?p=56ec69caaf923118098b456c&amp;controls=false&amp;autostart=false\" style=\"width:100%; height:768px\"></iframe></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><a href=\"https://www.tynker.com/ide/?p=56ec69caaf923118098b456c#close-modal\">Nh&igrave;n c&aacute;ch hoạt động của thuật to&aacute;n</a></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Lợi &iacute;ch của tư duy thuật to&aacute;n</strong><br />\r\nTư duy thuật to&aacute;n, hoặc khả năng x&aacute;c định c&aacute;c bước r&otilde; r&agrave;ng để giải quyết vấn đề, l&agrave; rất quan trọng trong c&aacute;c m&ocirc;n học như to&aacute;n học v&agrave; khoa học. Trẻ em sử dụng c&aacute;c thuật to&aacute;n m&agrave; kh&ocirc;ng nhận ra n&oacute; mọi l&uacute;c, đặc biệt l&agrave; trong to&aacute;n học. Để giải quyết vấn đề ph&acirc;n chia d&agrave;i, trẻ em &aacute;p dụng một thuật to&aacute;n m&agrave; ch&uacute;ng đ&atilde; học để lặp lại th&ocirc;ng qua c&aacute;c chữ số của số m&agrave; ch&uacute;ng chia. Đối với mỗi chữ số của cổ tức (số được chia), đứa trẻ phải chia, nh&acirc;n v&agrave; trừ. Tư duy thuật to&aacute;n cho ph&eacute;p trẻ em ph&aacute; vỡ c&aacute;c vấn đề v&agrave; kh&aacute;i niệm h&oacute;a c&aacute;c giải ph&aacute;p theo c&aacute;c bước ri&ecirc;ng biệt trong một quy tr&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trẻ em c&oacute; thể củng cố c&aacute;c kỹ năng tư duy thuật to&aacute;n bằng c&aacute;ch ho&agrave;n th&agrave;nh c&aacute;c hoạt động m&atilde; h&oacute;a tr&ecirc;n trang Giờ M&atilde; của ch&uacute;ng t&ocirc;i. Để ho&agrave;n th&agrave;nh c&aacute;c c&acirc;u đố, trẻ em thiết kế c&aacute;c thuật to&aacute;n đơn giản dựa tr&ecirc;n tr&igrave;nh tự, lặp lại v&agrave; logic c&oacute; điều kiện để giải quyết c&aacute;c vấn đề th&uacute; vị. Như với tất cả c&aacute;c kỹ năng, trẻ em c&oacute; thể cải thiện tư duy thuật to&aacute;n th&ocirc;ng qua thực h&agrave;nh h&agrave;ng ng&agrave;y v&agrave; bằng c&aacute;ch ho&agrave;n th&agrave;nh c&aacute;c dự &aacute;n s&aacute;ng tạo để &aacute;p dụng c&aacute;c kỹ năng của m&igrave;nh. Để t&igrave;m hiểu th&ecirc;m về thuật to&aacute;n v&agrave; giải quyết vấn đề, h&atilde;y thử đăng k&yacute; c&aacute;c kh&oacute;a học của ch&uacute;ng t&ocirc;i!</span></span></span></p>\r\n','/media/algorithm-blog.png','2019-05-07 12:35:32.972288','2019-05-07 16:07:37.469930',6,0,1,'',7,1,1),(8,'(P2) CÁCH GIẢI THÍCH THUẬT TOÁN CHO TRẺ EM',NULL,'<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/07/2016-03-28-2.png\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Hiểu c&aacute;c thuật to&aacute;n cơ bản mang lại sức mạnh cho cuộc sống số của bạn</strong><br />\r\nC&aacute;c thuật to&aacute;n cung cấp một loạt c&aacute;c hướng dẫn m&agrave; m&aacute;y t&iacute;nh tu&acirc;n theo để đi đến c&acirc;u trả lời v&agrave; ch&uacute;ng l&agrave;m nền tảng cho tất cả c&ocirc;ng nghệ của ch&uacute;ng t&ocirc;i. Vấn đề m&agrave; thuật to&aacute;n đang giải quyết c&oacute; thể l&agrave; sắp xếp danh s&aacute;ch, n&eacute;n tệp hoặc x&aacute;c định trang Internet n&agrave;o ph&ugrave; hợp nhất với bạn khi bạn t&igrave;m kiếm thứ g&igrave; đ&oacute;. C&aacute;c thuật to&aacute;n x&aacute;c định c&aacute;ch t&iacute;n hiệu giao th&ocirc;ng được l&ecirc;n lịch, c&aacute;ch dịch vụ bưu ch&iacute;nh c&oacute; thể ph&acirc;n phối thư của bạn một c&aacute;ch hiệu quả nhất v&agrave; hơn thế nữa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong thời đại kỹ thuật số, trẻ em n&ecirc;n l&agrave;m nhiều hơn l&agrave; chỉ sử dụng c&ocirc;ng nghệ. Họ cần kh&aacute;m ph&aacute; c&aacute;c thuật to&aacute;n cung cấp năng lượng cho điện thoại, c&aacute;c trang truyền th&ocirc;ng x&atilde; hội v&agrave; thế giới của họ để họ c&oacute; thể th&agrave;nh thạo m&atilde; h&oacute;a, ng&ocirc;n ngữ c&ocirc;ng nghệ v&agrave; đ&oacute;ng vai tr&ograve; t&iacute;ch cực trong việc tạo ra c&ocirc;ng nghệ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Thuật to&aacute;n t&igrave;m kiếm v&agrave; đề xuất</strong><br />\r\nKhi bạn nhập t&igrave;m kiếm v&agrave;o Google, n&oacute; sử dụng thuật to&aacute;n rất tinh vi để x&aacute;c định trang n&agrave;o tr&ecirc;n internet c&oacute; li&ecirc;n quan đến t&igrave;m kiếm của bạn v&agrave; xếp hạng ch&uacute;ng dựa tr&ecirc;n mức độ ph&ugrave; hợp v&agrave; uy t&iacute;n của ch&uacute;ng. Về cốt l&otilde;i, thuật to&aacute;n PageRank t&iacute;nh đến số lượng trang web kh&aacute;c li&ecirc;n kết đến một trang web nhất định cũng như thứ hạng của c&aacute;c trang web li&ecirc;n kết để x&aacute;c định thứ hạng của trang đ&oacute;. V&agrave; n&oacute; trả về kết quả của bạn trong chưa đầy một gi&acirc;y!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thuật to&aacute;n đề xuất c&aacute;c kết nối tr&ecirc;n phương tiện truyền th&ocirc;ng x&atilde; hội (như khuyến nghị của những người m&agrave; bạn c&oacute; thể biết tr&ecirc;n Facebook hoặc Linkedin) hoạt động bằng c&aacute;ch t&iacute;nh to&aacute;n mức độ t&aacute;ch biệt của bạn với những người d&ugrave;ng kh&aacute;c. Nếu bạn l&agrave; bạn của Amy v&agrave; Amy l&agrave; bạn của Ben, Facebook cho rằng bạn cũng c&oacute; thể biết Ben v&agrave; giới thiệu anh ta như một kết nối tiềm năng. Nhưng nếu Amy l&agrave; bạn với Ben v&agrave; Ben l&agrave; bạn với Charlene v&agrave; Charlene l&agrave; bạn với Daryl v&agrave; Daryl l&agrave; bạn với Erin, Facebook kh&ocirc;ng c&oacute; khả năng giới thiệu Erin như một kết nối. Tr&ecirc;n thực tế, c&aacute;c nh&agrave; khoa học đ&atilde; đưa ra giả thuyết rằng, bởi v&igrave; tất cả ch&uacute;ng ta kết nối to&agrave;n cầu như thế n&agrave;o, chỉ c&oacute; s&aacute;u độ c&aacute;ch biệt giữa bất kỳ hai người n&agrave;o tr&ecirc;n tr&aacute;i đất!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">V&agrave; khi bạn sử dụng Amazon hoặc Netflix, họ khuy&ecirc;n bạn n&ecirc;n mua hoặc c&aacute;c chương tr&igrave;nh kh&aacute;c m&agrave; bạn c&oacute; thể quan t&acirc;m dựa tr&ecirc;n thuật to&aacute;n lọc cộng t&aacute;c cố gắng dự đo&aacute;n những g&igrave; người d&ugrave;ng sẽ th&iacute;ch dựa tr&ecirc;n lựa chọn của những người d&ugrave;ng kh&aacute;c c&oacute; hồ sơ tương tự.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Thuật to&aacute;n sắp xếp</strong><br />\r\nBạn c&oacute; thể kh&ocirc;ng nghĩ về n&oacute; khi bạn sắp xếp mọi thứ trong cuộc sống thực, nhưng bạn đang theo một thuật to&aacute;n! M&aacute;y t&iacute;nh cần sắp xếp danh s&aacute;ch với h&agrave;ng triệu gi&aacute; trị cực kỳ nhanh ch&oacute;ng, v&igrave; vậy thuật to&aacute;n sắp xếp rất quan trọng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bạn c&oacute; thể m&ocirc; tả l&agrave;m thế n&agrave;o bạn c&oacute; thể sắp xếp mười cuốn s&aacute;ch theo thứ tự bảng chữ c&aacute;i? Nếu bạn phải n&oacute;i với ai đ&oacute; đ&atilde; kh&ocirc;ng hiểu về qu&aacute; tr&igrave;nh sắp xếp c&aacute;ch sắp xếp c&aacute;c cuốn s&aacute;ch, bạn sẽ viết những bước n&agrave;o?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bạn c&oacute; thể bắt đầu với một cuốn s&aacute;ch, sau đ&oacute; th&ecirc;m một cuốn s&aacute;ch tại một thời điểm, đặt n&oacute; v&agrave;o vị tr&iacute; th&iacute;ch hợp trong đội h&igrave;nh của bạn. Nhưng h&atilde;y tưởng tượng sẽ mất bao nhi&ecirc;u giờ hoặc nhiều năm nếu bạn phải l&agrave;m điều n&agrave;y với h&agrave;ng trăm, h&agrave;ng ng&agrave;n hoặc thậm ch&iacute; h&agrave;ng triệu cuốn s&aacute;ch! Thuật to&aacute;n n&agrave;y được gọi l&agrave; Sắp xếp ch&egrave;n v&agrave; n&oacute; hoạt động tốt cho c&aacute;c danh s&aacute;ch nhỏ, nhưng mất nhiều thời gian cho c&aacute;c danh s&aacute;ch d&agrave;i hơn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Một c&aacute;ch kh&aacute;c bạn c&oacute; thể sắp xếp mười cuốn s&aacute;ch l&agrave; sắp xếp ch&uacute;ng theo thứ tự ngẫu nhi&ecirc;n, sau đ&oacute; đi xuống danh s&aacute;ch so s&aacute;nh c&aacute;c cuốn s&aacute;ch nằm cạnh nhau. Đầu ti&ecirc;n bạn sẽ so s&aacute;nh cuốn s&aacute;ch thứ nhất v&agrave; thứ hai. Nếu ch&uacute;ng sai thứ tự, bạn trao đổi ch&uacute;ng. Sau đ&oacute;, bạn so s&aacute;nh c&aacute;c cuốn s&aacute;ch thứ hai v&agrave; thứ ba, v&agrave; một lần nữa quyết định c&oacute; n&ecirc;n trao đổi ch&uacute;ng. Bất cứ khi n&agrave;o bạn đến cuối danh s&aacute;ch, bạn lại quay trở lại danh s&aacute;ch, mỗi lần chỉ so s&aacute;nh những cuốn s&aacute;ch nằm ngay cạnh nhau. Đ&acirc;y được gọi l&agrave; Thuật to&aacute;n Sắp xếp Bong b&oacute;ng v&agrave; một lần nữa hợp l&yacute; cho c&aacute;c danh s&aacute;ch nhỏ, nhưng rất kh&ocirc;ng hiệu quả đối với c&aacute;c danh s&aacute;ch d&agrave;i hơn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Dự &aacute;n n&agrave;y cho thấy một c&aacute;ch để thực hiện sắp xếp Bong b&oacute;ng, với c&aacute;c mục trong danh s&aacute;ch được ho&aacute;n đổi để tự sắp xếp:</span></span></span></p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"//www.tynker.com/ide/embedded?p=5cd11b3370b002393e237c79&amp;controls=false&amp;autostart=false\" style=\"width:100%; height:768px\"></iframe></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\"><a href=\"https://www.tynker.com/ide/?p=5cd11b3370b002393e237c79\"><span style=\"color:#d35400\">Xem chi tiết thuật to&aacute;n</span></a></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\"><strong>C&aacute;c thuật to&aacute;n trong cuộc sống của bạn</strong><br />\r\nC&aacute;c thuật to&aacute;n hiệu quả để t&igrave;m kiếm v&agrave; sắp xếp l&agrave; rất quan trọng để x&acirc;y dựng phần mềm chạy nhanh. Những thuật to&aacute;n cơ bản n&agrave;y v&agrave; c&aacute;c thuật to&aacute;n cơ bản kh&aacute;c l&agrave;m nền tảng cho hầu hết c&ocirc;ng nghệ của bạn, từ video YouTube bạn ph&aacute;t trực tiếp đến điện thoại, đến nhận diện khu&ocirc;n mặt tr&ecirc;n m&aacute;y ảnh của bạn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\">L&agrave;m thế n&agrave;o để YouTube ph&aacute;t trực tiếp v&agrave;o điện thoại của bạn nhanh v&agrave; liền mạch như vậy? L&agrave;m thế n&agrave;o để m&aacute;y ảnh của bạn t&igrave;m kiếm khu&ocirc;n mặt, tất cả tr&ocirc;ng rất kh&aacute;c nhau t&ugrave;y thuộc v&agrave;o người, &aacute;nh s&aacute;ng v&agrave; khoảng c&aacute;ch?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\">C&oacute; vẻ như m&aacute;y t&iacute;nh của bạn đang đưa ra những quyết định th&ocirc;ng minh m&agrave; ch&uacute;ng ta thường nghĩ l&agrave; con người duy nhất, như tạo ra c&aacute;c kết nối v&agrave; phỏng đo&aacute;n c&oacute; gi&aacute;o dục. Nhưng mỗi trong số n&agrave;y được cung cấp bởi một thuật to&aacute;n rất phức tạp để x&aacute;c định c&aacute;ch ho&agrave;n th&agrave;nh tốt nhất nhiệm vụ bằng c&aacute;ch sử dụng x&aacute;c suất v&agrave; logic.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:20px\">Nếu con bạn c&oacute; thể hiểu v&agrave; viết c&aacute;c thuật to&aacute;n, điều n&agrave;y mở ra cả một thế giới khả năng. N&oacute; cho ph&eacute;p họ kh&ocirc;ng chỉ sử dụng c&aacute;c thuật to&aacute;n n&agrave;y hiệu quả hơn m&agrave; c&ograve;n bắt đầu viết c&aacute;c chương tr&igrave;nh v&agrave; thuật to&aacute;n của ri&ecirc;ng họ v&agrave; giải quyết c&aacute;c vấn đề c&oacute; &yacute; nghĩa với m&atilde;.</span></span></span></p>\r\n','/media/2016-03-28-2.png','2019-05-07 12:49:46.674987','2019-05-07 12:49:46.674993',5,0,1,'',7,1,1),(9,'KỸ NĂNG 4C CHO THẾ KỶ 21: TƯ DUY PHẢN BIỆN',NULL,'<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/07/4c__critical-thinking_blog.png\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#e74c3c\"><span style=\"font-size:28px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>KỸ NĂNG 4C CHO THẾ KỶ 21: TƯ DUY PHẢN BIỆN</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">M&ugrave;a lễ n&agrave;y, h&atilde;y cho những người trẻ tuổi trong cuộc sống của bạn cơ hội ph&aacute;t triển c&aacute;c kỹ năng quan trọng của thế kỷ 21 như tư duy ph&ecirc; ph&aacute;n v&agrave; giải quyết vấn đề! Trong phần thứ hai của loạt 4 C Cấm của ch&uacute;ng t&ocirc;i (đ&atilde; bỏ lỡ b&agrave;i viết về sự s&aacute;ng tạo của ch&uacute;ng t&ocirc;i?), Bạn sẽ học c&aacute;ch Tynker gi&uacute;p trẻ ph&aacute;t triển c&aacute;c kỹ năng tư duy ph&ecirc; ph&aacute;n khi ch&uacute;ng giải quyết c&aacute;c th&aacute;ch thức bằng c&aacute;ch sử dụng m&atilde; h&oacute;a v&agrave; ng&ocirc;n ngữ lập tr&igrave;nh trong thế giới thực.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Hiểu mối quan hệ nguy&ecirc;n nh&acirc;n v&agrave; kết quả, v&agrave; đưa ra lựa chọn chiến lược dựa tr&ecirc;n kiến ​​thức n&agrave;y.</strong> Khi một đứa trẻ thao t&uacute;ng m&atilde; dựa tr&ecirc;n khối trong Tynker, ch&uacute;ng phải chọn c&aacute;c khối m&atilde; sẽ cho ch&uacute;ng kết quả m&agrave; ch&uacute;ng muốn. Tương tự như vậy, khi một lập tr&igrave;nh vi&ecirc;n n&acirc;ng cao đang viết m&atilde; trong Tr&igrave;nh chỉnh sửa m&atilde; Tynker, họ biết rằng c&aacute;c lỗi trong c&uacute; ph&aacute;p m&atilde; dẫn đến c&aacute;c vấn đề trong dự &aacute;n m&agrave; họ tạo ra.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trẻ em hiểu rằng, kh&ocirc;ng giống như giao tiếp bằng hầu hết c&aacute;c ng&ocirc;n ngữ n&oacute;i, nếu xảy ra lỗi khi viết bằng ng&ocirc;n ngữ lập tr&igrave;nh, người nhận tin nhắn, m&aacute;y t&iacute;nh kh&ocirc;ng thể y&ecirc;u cầu l&agrave;m r&otilde; hoặc hiểu &yacute; nghĩa của ch&uacute;ng t&ocirc;i dựa tr&ecirc;n ngữ cảnh. C&oacute; một mức độ cụ thể cao hơn, ch&iacute;nh x&aacute;c, bắt buộc. Điều n&agrave;y tương tự như thực hiện đ&uacute;ng thứ tự c&aacute;c ph&eacute;p to&aacute;n khi ho&agrave;n th&agrave;nh một b&agrave;i to&aacute;n: Nếu một học sinh kh&ocirc;ng giải b&agrave;i to&aacute;n bằng c&aacute;ch sử dụng đ&uacute;ng thứ tự c&aacute;c h&agrave;m, c&acirc;u trả lời sẽ kh&ocirc;ng ch&iacute;nh x&aacute;c.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Thực h&agrave;nh s&aacute;ng tạo bằng c&aacute;ch thực hiện c&aacute;c dự &aacute;n ban đầu.</strong> C&aacute;c hoạt động của Tynker c&oacute; c&aacute;c hướng dẫn m&agrave; trẻ em theo d&otilde;i để ho&agrave;n th&agrave;nh c&aacute;c nhiệm vụ của dự &aacute;n, chẳng hạn như nh&acirc;n bản l&aacute; trong một trong c&aacute;c dự &aacute;n theo m&ugrave;a gần đ&acirc;y của ch&uacute;ng t&ocirc;i. Nhưng khi c&aacute;c lập tr&igrave;nh vi&ecirc;n thiết kế một dự &aacute;n ban đầu ở Tynker, nguồn cảm hứng kh&aacute;c nhau từ c&aacute;c động vật v&agrave; ng&ocirc;i sao thể thao y&ecirc;u th&iacute;ch đến c&aacute;c nh&acirc;n vật hư cấu, c&acirc;u chuyện, v&agrave; hơn thế nữa. Để đưa tầm nh&igrave;n s&aacute;ng tạo của họ v&agrave;o cuộc sống, trẻ em cần r&uacute;t ra hiểu biết về c&aacute;ch c&aacute;c khối m&atilde; hoặc d&ograve;ng m&atilde; hoạt động v&agrave; sau đ&oacute; thử nghiệm với c&aacute;c yếu tố đ&oacute;. Ch&iacute;nh trong kh&ocirc;ng gian s&aacute;ng tạo n&agrave;y, trẻ em c&oacute; thể chấp nhận rủi ro v&agrave; điều chỉnh lại c&aacute;c chiến lược m&atilde; h&oacute;a theo &yacute; muốn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Tổng hợp: Thực hiện c&aacute;c dự &aacute;n tuyệt vời bằng c&aacute;ch x&acirc;y dựng tr&ecirc;n c&aacute;c &yacute; tưởng kh&aacute;c của người kh&aacute;c.</strong> Tynker cộng đồng cộng đồng cho ph&eacute;p người d&ugrave;ng khai th&aacute;c sự gi&agrave;u c&oacute; của trải nghiệm m&atilde; h&oacute;a được cung cấp bởi c&aacute;c lập tr&igrave;nh vi&ecirc;n trẻ tr&ecirc;n to&agrave;n cầu. Trẻ em c&oacute; thể lựa chọn phối lại c&aacute;c dự &aacute;n kh&aacute;c v&agrave; lưu một bản sao mới của sản phẩm, được c&aacute; nh&acirc;n h&oacute;a theo tầm nh&igrave;n của ch&uacute;ng về những g&igrave; m&atilde; c&oacute; thể thực hiện trong một dự &aacute;n nhất định. V&agrave; phối lại c&aacute;c dự &aacute;n kh&aacute;c mang đến cho trẻ em một nguồn cảm hứng kh&aacute;c cho c&aacute;c dự &aacute;n tương lai của ch&iacute;nh ch&uacute;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bằng c&aacute;ch ph&aacute;t triển c&aacute;c kỹ năng tư duy ph&ecirc; ph&aacute;n th&ocirc;ng qua m&atilde; h&oacute;a, trẻ em đang chuẩn bị cho th&agrave;nh c&ocirc;ng ở trường v&agrave; sự nghiệp tương lai của ch&uacute;ng. Hầu hết c&aacute;c m&ocirc;n học li&ecirc;n quan đến cả hai quy tắc sau đ&acirc;y v&agrave; sử dụng ch&uacute;ng một c&aacute;ch s&aacute;ng tạo để tạo ra c&aacute;c sản phẩm mới. V&iacute; dụ, trong c&aacute;c lớp to&aacute;n v&agrave; tiếng Anh, học sinh cần học c&aacute;c c&ocirc;ng thức (nghĩ: thứ tự c&aacute;c thao t&aacute;c, c&aacute;ch viết c&acirc;u) v&agrave; hiểu c&aacute;ch sử dụng c&aacute;c quy tắc đ&oacute; để tạo ra c&aacute;c giải ph&aacute;p duy nhất khi giải c&aacute;c b&agrave;i to&aacute;n trong thế giới thực hoặc tự viết c&acirc;u chuyện.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tương lai của ch&uacute;ng ta với tư c&aacute;ch l&agrave; một x&atilde; hội sẽ mang đến nhiều th&aacute;ch thức v&agrave; trẻ em sẽ sẵn s&agrave;ng đ&oacute;ng g&oacute;p hơn khi ch&uacute;ng c&oacute; thể đ&aacute;nh gi&aacute; dữ liệu c&oacute; sẵn v&agrave; đưa ra giải ph&aacute;p th&ocirc;ng qua thử v&agrave; sai. Con bạn sẽ sử dụng c&aacute;c kỹ năng tư duy ph&ecirc; ph&aacute;n của m&igrave;nh để đưa ra những c&aacute;ch mới để tạo ra năng lượng sạch? Hoặc tận dụng c&ocirc;ng nghệ robot để gi&uacute;p đỡ người gi&agrave;?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tynker cung cấp hơn 2.000 hoạt động hướng đến sở th&iacute;ch để giới thiệu cho trẻ em về thế giới m&atilde; h&oacute;a th&uacute; vị. Cho d&ugrave; họ muốn chiến đấu với bạn b&egrave; trong Crystal Clash, chỉnh sửa trang Python hoặc kh&aacute;m ph&aacute; sự nghiệp trong khi m&atilde; h&oacute;a với Barbie, m&oacute;n qu&agrave; của một thu&ecirc; bao Tynker sẽ gi&uacute;p họ ph&aacute;t triển c&aacute;c kỹ năng tư duy ph&ecirc; ph&aacute;n m&agrave; họ sẽ cần ngay b&acirc;y giờ!</span></span></span></p>\r\n','/media/4c__critical-thinking_blog.png','2019-05-07 16:15:27.877813','2019-05-07 16:15:27.877819',5,0,1,'',7,1,1),(10,'DỰ ÁN HAY ĐỊNH KỲ','1','<p style=\"text-align:center\"><img alt=\"\" src=\"/media/2019/05/07/05-01-2019-seasonal-projects-blog.png\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:36px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>ĐIỂM NỔI BẬT CỦA DỰ &Aacute;N THEO M&Ugrave;A</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kể từ đầu năm nay, c&aacute;c nh&agrave; sản xuất Tynker đ&atilde; m&atilde; h&oacute;a hơn 1.779.488 dự &aacute;n! Trong số c&aacute;c dự &aacute;n n&agrave;y c&oacute; c&aacute;c dự &aacute;n theo m&ugrave;a rất được y&ecirc;u th&iacute;ch, nơi trẻ em thực h&agrave;nh tư duy ph&ecirc; ph&aacute;n v&agrave; kỹ năng s&aacute;ng tạo khi ch&uacute;ng biến &yacute; tưởng của m&igrave;nh th&agrave;nh c&aacute;c dự &aacute;n m&atilde; h&oacute;a m&agrave; ch&uacute;ng x&acirc;y dựng v&agrave; chia sẻ!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong b&agrave;i viết n&agrave;y, ch&uacute;ng t&ocirc;i muốn l&agrave;m nổi bật chỉ một v&agrave;i dự &aacute;n xuất sắc được cộng đồng c&ocirc;ng bố. Ch&uacute;ng t&ocirc;i đ&atilde; thấy một loạt c&aacute;c dự &aacute;n xuất sắc bắt nguồn từ c&aacute;c mẫu theo m&ugrave;a, bao gồm Th&aacute;ng Hai Tr&aacute;i Tim Kẹo, Nghệ thuật Ng&agrave;y Th&aacute;ng Ba Pi, C&acirc;y Tr&ograve;n v&agrave; C&acirc;y Thỏ Th&aacute;ng Tư, v&agrave; dự &aacute;n Th&aacute;ng Lịch sử Đen của ch&uacute;ng t&ocirc;i!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Con bạn đ&atilde; thử c&aacute;c dự &aacute;n theo m&ugrave;a n&agrave;y chưa? Chia sẻ những dự &aacute;n n&agrave;y với con bạn v&agrave; truyền cảm hứng cho ch&uacute;ng để tự l&agrave;m! Nhấp v&agrave;o h&igrave;nh ảnh cho mỗi dự &aacute;n v&agrave; cho con bạn đăng nhập v&agrave;o Tynker để xem trang dự &aacute;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Tr&aacute;i tim kẹo</strong><br />\r\nTrong dự &aacute;n n&agrave;y, được gửi cho ng&agrave;y Valentine m&atilde; h&oacute;a, trẻ em t&ugrave;y chỉnh v&agrave; x&acirc;y dựng tr&ograve; chơi tr&aacute;i tim kẹo của ri&ecirc;ng m&igrave;nh. Họ c&oacute; thể viết th&ocirc;ng điệp của ri&ecirc;ng m&igrave;nh l&ecirc;n tr&aacute;i tim kẹo, thay đổi h&igrave;nh nền v&agrave; lập tr&igrave;nh chuyển động của hộp kẹo!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/07/candy_hearts_treasure-768x434.jpg\" style=\"width:100%\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><strong><a href=\"https://www.tynker.com/ide/?p=5c4fa5ce04605829a70efa1c\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tham khảo</span></span></span></a></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nghệ thuật ng&agrave;y Pi</span></span></span></strong><br />\r\n<span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Với dự &aacute;n n&agrave;y được gửi v&agrave;o ng&agrave;y 14 th&aacute;ng 3, c&ograve;n được gọi l&agrave; Ng&agrave;y Pi, trẻ em đ&atilde; m&atilde; h&oacute;a c&aacute;c chữ số của Pi để xuất hiện c&ugrave;ng một l&uacute;c để tạo ra một thiết kế th&uacute; vị! Đối với dự &aacute;n n&agrave;y, con bạn c&oacute; thể chọn ng&ocirc;n ngữ lập tr&igrave;nh để sử dụng. T&ugrave;y thuộc v&agrave;o cấp độ của họ, họ c&oacute; thể x&acirc;y dựng c&ugrave;ng một dự &aacute;n bằng bất kỳ ng&ocirc;n ngữ m&atilde; h&oacute;a n&agrave;o trong số ba ng&ocirc;n ngữ sau: m&atilde; h&oacute;a khối, JavaScript hoặc Python! Như một phần thưởng, họ thậm ch&iacute; c&oacute; thể l&agrave;m cho c&aacute;c chữ số xuất hiện theo h&igrave;nh xoắn ốc!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kiểm tra c&aacute;ch người d&ugrave;ng n&agrave;y l&agrave;m cho số của họ xuất hiện li&ecirc;n tiếp trong một h&igrave;nh dạng m&aacute;t mẻ!</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/07/pi_day_draconian-768x434.jpg\" style=\"width:100%\" /></span></span></span></strong></p>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://www.tynker.com/ide/?p=5cd150c9745044377c20550d\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tham khảo</span></span></span></strong></a></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trồng c&acirc;y</span></span></span></strong><br />\r\n<span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Được gửi v&agrave;o Ng&agrave;y Tr&aacute;i đất, với dự &aacute;n n&agrave;y, con bạn c&oacute; thể lập tr&igrave;nh một tr&ograve; chơi vui nhộn, nơi ch&uacute;ng trồng c&acirc;y để giảm mức CO2 trong kh&iacute; quyển. Từ bảng điều khiển của cha mẹ, bạn c&oacute; thể chia sẻ c&aacute;c tr&ograve; chơi của họ với bạn b&egrave; v&agrave; gia đ&igrave;nh để nhắc nhở họ tạo ra t&aacute;c động t&iacute;ch cực đến h&agrave;nh tinh! C&aacute;c lập tr&igrave;nh vi&ecirc;n c&oacute; thể thực hiện dự &aacute;n n&agrave;y bằng c&aacute;ch sử dụng m&atilde; h&oacute;a khối hoặc Python.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; sản xuất n&agrave;y đ&atilde; loại bỏ c&aacute;c yếu tố h&igrave;nh ảnh kh&aacute;c để tạo ra một nền tảng th&uacute; vị!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/media/2019/05/07/plant_trees_background-768x432.jpg\" style=\"width:100%\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://www.tynker.com/ide/?p=5cd1515b60ba6f57827bd83f\"><strong><span style=\"color:#d35400\"><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tham khảo</span></span></span></strong></a></p>\r\n','/media/05-01-2019-seasonal-projects-blog.png','2019-05-07 16:39:34.749515','2019-05-07 16:39:34.749521',5,0,1,'',7,1,1),(11,'CÁCH SỬ DỤNG LCD I2C','Hướng dẫn sử dụng LCD I2C','<p>Ch&agrave;o Thầy/C&ocirc;, em c&oacute; thắc mắc về 1 kiến thức của arduino: em kh&ocirc;ng hiểu v&agrave; kh&ocirc;ng thực h&agrave;nh th&agrave;nh c&ocirc;ng hiển thị nội dung l&ecirc;n m&agrave;n h&igrave;nh LCD</p>\r\n','/media/51k8HEbEpKL._SX342_.jpg','2019-05-08 23:40:54.983891','2019-05-08 23:40:54.983897',3,0,1,'',37,3,NULL);
