INSERT INTO `forumreply` VALUES (1,'<p>C&agrave;m ơn Anh/Chị. Em đ&atilde; l&agrave;m được rồi ạ.</p>\n','2019-04-28 17:33:00.891487','2019-04-28 17:33:00.891499',1,'',5,1),(2,'<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng nhất thiết phải d&ugrave;ng mỏ h&agrave;ng đ&acirc;u nha em , ở chốt của linh kiện c&oacute; phần lồi ra để ch&uacute;ng ta nối d&acirc;y m&agrave; em.</span></span></span></span></p>\n','2019-04-28 17:35:49.387578','2019-04-28 17:35:49.387585',1,'',5,2),(3,'<p>Thanks!</p>\n','2019-05-08 09:31:00.427859','2019-05-08 09:31:00.427867',1,'',3,4),(5,'<p>good!</p>\n','2019-05-08 13:59:47.831571','2019-05-08 13:59:47.831588',1,'',5,2),(6,'<p>Bạn th&ecirc;m <strong>Delay</strong> v&agrave;o sau thủ tục&nbsp;<strong>hienthithoigian</strong> <strong>setValue</strong></p>\n','2019-05-08 21:19:01.294591','2019-05-08 21:19:01.294601',1,'',42,4),(7,'<p>c&aacute;c dự &aacute;n định kỳ rất hay. hy vọng ad sẽ cập nh&acirc;t h&agrave;ng tuần cho tụi em</p>\n','2019-05-09 11:13:22.442868','2019-05-09 11:13:22.442875',1,'',38,10),(8,'<p>rất hay</p>\n','2019-05-09 11:13:46.296254','2019-05-09 11:13:46.296261',1,'',38,10),(9,'<p>tin tức hay v&agrave; hấp dẫn</p>\n','2019-05-09 11:14:24.314709','2019-05-09 11:14:24.314715',1,'',38,9),(10,'<p>đề t&agrave;i rất hay v&agrave; thu h&uacute;t</p>\n','2019-05-09 11:15:37.766286','2019-05-09 11:15:37.766291',1,'',38,7),(11,'<p>diễn đ&agrave;n tin tức rất hay. cung cấp nhiều nội dung hay.</p>\n','2019-05-09 11:17:18.362784','2019-05-09 11:17:18.362791',1,'',42,8),(12,'<p>C&agrave;m ơn Anh/Chị. Em đ&atilde; l&agrave;m được rồi ạ.</p>\n','2019-05-09 11:17:48.727419','2019-05-09 11:17:48.727426',1,'',42,1);
